-- ----------------------------
-- Table structure for `news`
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news` (
  `news_id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `description` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `content` text COLLATE utf8_unicode_ci NOT NULL,
  `image_link` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `status` smallint(6) NOT NULL DEFAULT '1',
  `created_at` int(11) NOT NULL,
  `updated_at` int(11) NOT NULL,
  PRIMARY KEY (`news_id`)
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- ----------------------------
-- Records of news
-- ----------------------------
INSERT INTO news VALUES ('3', 'Cách mua quần áo trên mạng an toàn - tiện lợi và tiết kiệm', 'Công nghệ thông tin ngày càng phát triển cho phép bạn mua hàng nhanh chóng qua mạng. Đồ gia dụng, dụng cụ học tập, thực phẩm và cả quần áo. Đối với các mặt hàng khác sẽ khó nhận biết được độ thật giả và chất lượng, chỉ sau một thời gian dài sử dụng mới có', '<p><span style=\"font-family: \'times new roman\', times, serif;\">C&ocirc;ng nghệ th&ocirc;ng tin ng&agrave;y c&agrave;ng ph&aacute;t triển cho ph&eacute;p bạn mua h&agrave;ng nhanh ch&oacute;ng qua mạng. Đồ gia dụng, dụng cụ học tập, thực phẩm v&agrave; cả quần &aacute;o. Đối với c&aacute;c mặt h&agrave;ng kh&aacute;c sẽ kh&oacute; nhận biết được độ thật giả v&agrave; chất lượng, chỉ sau một thời gian d&agrave;i sử dụng mới c&oacute; thể nhận ra. Nhưng với mặt h&agrave;ng thời trang, sự thất vọng hay vui sướng sẽ hiện ngay tr&ecirc;n khu&ocirc;n mặt bạn khi vừa nhận h&agrave;ng.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Mua quần &aacute;o qua Internet đ&atilde; trở th&agrave;nh nỗi &aacute;m ảnh của nhiều người bởi kh&ocirc;ng phải ai cũng mua được những m&oacute;n đồ ưng &yacute; ngay từ lần mua sắm Online đầu ti&ecirc;n.</span></p>\r\n<p align=\"center\"><span style=\"font-family: \'times new roman\', times, serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/mua-quan-ao-qua-mang.jpg\" alt=\"mua-quan-ao-qua-mang\" /></em></span><br /><span style=\"font-family: \'times new roman\', times, serif;\"><em>C&oacute; rất nhiều người &ldquo;nghiện&rdquo; mua quần &aacute;o qua mạng bởi kh&ocirc;ng phải chen lấn, x&ocirc; đẩy hay mất thời gian đến c&aacute;c cửa h&agrave;ng xem đồ; k&egrave;m theo đ&oacute; l&agrave; nhiều lợi &iacute;ch như tiết kiệm tiền mua quần &aacute;o với c&aacute;c đợt giảm gi&aacute; chỉ d&agrave;nh cho kh&aacute;ch mua Online, <a href=\"http://canifa.com/khach-hang-than-thiet.html/\" target=\"_blank\">chương tr&igrave;nh kh&aacute;ch h&agrave;ng th&acirc;n thiết</a>, ship miễn ph&iacute; hay qu&agrave; tặng k&egrave;m.</em></span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Bạn muốn mua sắm quần &aacute;o trực tuyến nhưng chưa biết c&aacute;ch?</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Bạn muốn chọn được những sản phẩm &ldquo;đ&aacute;ng đồng tiền b&aacute;t gạo&rdquo;?</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">NHƯNG QUAN TRỌNG L&Agrave;...</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\"><strong>Bạn kh&ocirc;ng muốn trở th&agrave;nh nạn nh&acirc;n của những chi&ecirc;u tr&ograve; lừa đảo qua mạng khi mua sắm.</strong></span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Vậy cẩm nang n&agrave;y ch&iacute;nh l&agrave; &ldquo;cứu c&aacute;nh&rdquo; cho nhu cầu ch&iacute;nh đ&aacute;ng n&agrave;y của bạn.</span></p>\r\n<h2><span style=\"font-family: \'times new roman\', times, serif;\">Kinh nghiệm chọn mua quần &aacute;o qua mạng</span></h2>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/mua-quan-ao-qua-mang-tim-hieu.jpg\" alt=\"mua-quan-ao-qua-mang-tim-hieu\" /></em></span><br /><span style=\"font-family: \'times new roman\', times, serif;\"><em>Mua quần &aacute;o <a href=\"http://canifa.com/nu.html\" target=\"_blank\">thời trang</a> tr&ecirc;n mạng sẽ gi&uacute;p bạn tiết kiệm được thời gian, tiền bạc v&agrave; kh&ocirc;ng mất c&ocirc;ng di chuyển. Nhưng mua c&aacute;c sản phẩm thời trang Online vẫn tồn tại nhiều vấn đề m&agrave; bạn thực sự phải rất tỉnh t&aacute;o để nhận ra.</em></span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Khi mua Online, bạn chắc chắn bạn mua được bộ quần &aacute;o vừa với size của m&igrave;nh, tham khảo gi&aacute; tại nhiều cửa h&agrave;ng để so s&aacute;nh v&agrave; phải biết được một số kỹ năng để kh&ocirc;ng trở th&agrave;nh nạn nh&acirc;n bị lừa đảo qua mạng.</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Nắm chắc số đo của bạn</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">V&igrave; bạn kh&ocirc;ng thể thử quần &aacute;o nếu mua h&agrave;ng trực tuyến, v&igrave; vậy h&atilde;y nắm chắc số đo của m&igrave;nh để lựa chọn quần &aacute;o cho ph&ugrave; hợp. Mỗi nh&agrave; sản xuất đề đưa ra c&aacute;c k&iacute;ch thước kh&aacute;c nhau với độ lớn/trung b&igrave;nh/nhỏ. Xem số đo của bạn v&agrave; đối chiếu với c&aacute;c số đo đ&oacute;, bạn sẽ <a href=\"http://canifa.com/huong-dan-chon-size.html/\" target=\"_blank\">chọn được size quần &aacute;o ph&ugrave; hợp</a> với m&igrave;nh.</span></p>\r\n<ul>\r\n<li><span style=\"font-family: \'times new roman\', times, serif;\"><em>Đối với bạn nữ</em>, điều cơ bản nhất để chọn đ&uacute;ng quần &aacute;o khi mua quần &aacute;o online l&agrave; biết được số đo ba v&ograve;ng: ngực, eo, h&ocirc;ng; chiều cao v&agrave; c&acirc;n nặng của m&igrave;nh. Đ&ocirc;i khi, số đo c&aacute;nh tay hay chiều d&agrave;i lưng cũng cần thiết để mua những bộ quần &aacute;o c&oacute; thiết kế đặc biệt kh&aacute;c.</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times, serif;\"><em>Đối với bạn nam</em>, ngo&agrave;i chiều cao v&agrave; c&acirc;n nặng th&igrave; số đo v&ograve;ng ngực, cổ, lưng l&agrave; những số đo bạn cần biết khi chọn mua quần &aacute;o. Nếu chọn mua vest hay c&aacute;c loại &aacute;o cao cấp kh&aacute;c, bạn c&ograve;n phải biết th&ecirc;m về chiều d&agrave;i c&aacute;nh tay, chiều rộng của lưng&hellip;</span></li>\r\n<li><span style=\"font-family: \'times new roman\', times, serif;\"><em>Đối với trẻ em</em>, cha mẹ chỉ cần biết số đo ngực, chiều cao, c&acirc;n nặng của b&eacute; trai; nhưng đối với b&eacute; g&aacute;i cần biết th&ecirc;m số đo v&ograve;ng eo, v&ograve;ng h&ocirc;ng. C&ograve;n đối với trẻ sơ sinh, cha mẹ chỉ cần biết chiều cao v&agrave; c&acirc;n nặng của b&eacute; để chọn mua l&agrave; được.</span></li>\r\n</ul>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Ngo&agrave;i ra, bạn n&ecirc;n biết bạn đang mua quần &aacute;o cho m&ugrave;a n&agrave;o. Đối với nhiều người trong ch&uacute;ng ta, thời gian m&ugrave;a h&egrave; kh&aacute; dễ d&agrave;ng v&agrave; thuận lợi trong kh&acirc;u chọn đồ, nhưng v&agrave;o mua đ&ocirc;ng; đ&ocirc;i khi bạn sẽ phải chọn chiếc &aacute;o kho&aacute;c gi&oacute; rộng hơn với size của m&igrave;nh.</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Kiểm tra th&ocirc;ng tin về k&iacute;ch cỡ</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Ở một số Quốc gia sẽ c&oacute; một biểu đồ k&iacute;ch cỡ m&agrave; họ sử dụng cho tất cả c&aacute;c loại quần &aacute;o của họ, nhưng đối với một v&agrave;i trang b&aacute;n h&agrave;ng trực tuyến, họ sẽ b&aacute;n quần &aacute;o của rất nhiều h&atilde;ng kh&aacute;c nhau, với c&aacute;c ti&ecirc;u chuẩn của c&aacute;c nước kh&aacute;c nhau. V&iacute; dụ, c&ugrave;ng một chiếc &aacute;o len size XL, nhưng theo ti&ecirc;u chuẩn US l&agrave; cỡ 18, UK l&agrave; cỡ 22, Đức l&agrave; 46, Ph&aacute;p l&agrave; 50...V&igrave; vậy, h&atilde;y xem phần m&ocirc; tả sản phẩm, kiểm tra c&aacute;c k&iacute;ch thước v&agrave; số đo của loại quần &aacute;o bạn muốn mua.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Bạn c&oacute; thể thấy rằng, bạn n&ecirc;n chọn size nhỏ theo ti&ecirc;u chuẩn của nh&agrave; sản xuất nhưng tr&ecirc;n một phương diện kh&aacute;c, th&acirc;n h&igrave;nh của bạn lại vừa vặn với bộ quần &aacute;o c&oacute; k&iacute;ch cỡ lớn hơn.</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">L&ecirc;n danh s&aacute;ch những loại quần &aacute;o cần mua</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Nếu c&oacute; &yacute; định mua nhiều quần &aacute;o c&ugrave;ng một l&uacute;c, h&atilde;y viết tất cả mọi thứ bạn cần trước khi bắt đầu. L&agrave;m như vậy sẽ gi&uacute;p bạn đi đ&uacute;ng hướng v&agrave; kh&ocirc;ng bị cho&aacute;ng ngợp trước sự phong ph&uacute;, bạt ng&agrave;n mẫu m&atilde; của c&aacute;c sản phẩm thời trang.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">T&ugrave;y thuộc v&agrave;o nhu cầu, bạn c&oacute; thể v&agrave;o Website b&aacute;n h&agrave;ng Online của c&aacute;c thương hiệu thời trang m&igrave;nh y&ecirc;u th&iacute;ch hoặc mua tại c&aacute;c s&agrave;n Thương mại điện tử tổng hợp.</span></p>\r\n<blockquote>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\"><em>Kinh nghiệm</em>: Sử dụng C&ocirc;ng cụ t&igrave;m kiếm Google &ndash; <a href=\"http://google.com.vn/\">http://google.com.vn</a> để t&igrave;m ra địa chỉ Website, Fanpage của nh&atilde;n h&agrave;ng thời trang m&agrave; bạn y&ecirc;u th&iacute;ch. V&iacute; dụ: Bạn đ&aacute;nh v&agrave;o đ&oacute; canifa website hoặc canifa fanpage l&agrave; sẽ thấy Website của ch&uacute;ng t&ocirc;i.</span></p>\r\n</blockquote>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Kh&ocirc;ng la c&agrave;</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Mua sắm quần &aacute;o thời trang Online cũng giống như mua sắm tại c&aacute;c cửa h&agrave;ng. Sẽ c&oacute; rất nhiều mẫu quần &aacute;o được trưng b&agrave;y c&ugrave;ng l&uacute;c tr&ecirc;n Website b&aacute;n h&agrave;ng. Bạn n&ecirc;n biết kiềm chế bản th&acirc;n, kh&ocirc;ng la c&agrave; v&agrave;o c&aacute;c danh mục chứa c&aacute;c sản phẩm m&agrave; m&igrave;nh kh&ocirc;ng c&oacute; nhu cầu. Nếu kh&ocirc;ng bạn sẽ c&oacute; nguy cơ bị l&atilde;ng ph&iacute; thời gian nh&igrave;n v&agrave;o quần &aacute;o kh&ocirc;ng thực sự cần thiết hoặc sẽ mua th&ecirc;m những m&oacute;n đồ nằm ngo&agrave;i ng&acirc;n s&aacute;ch mua sắm của bạn.</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Thử quần &aacute;o ngay khi nhận được</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Nhiều đơn vị b&aacute;n quần &aacute;o qua mạng chấp nhận cho người mua h&agrave;ng trả lại nhưng chỉ trong một khoảng thời gian nhất định. V&igrave; vậy, h&atilde;y thử quần &aacute;o ngay khi bạn nhận được từ nh&acirc;n vi&ecirc;n giao h&agrave;ng v&agrave; đừng bỏ bất kỳ nh&atilde;n hay thẻ g&igrave; tr&ecirc;n bộ đồ, nếu kh&ocirc;ng rất c&oacute; thể bạn sẽ vi phạm v&agrave;o <a href=\"http://canifa.com/quy-dinh-doi-hang.html/\" target=\"_blank\">quy chế đổi trả h&agrave;ng</a> của họ v&agrave; kh&ocirc;ng được đổi lại sản phẩm đ&atilde; mua.</span></p>\r\n<h2><span style=\"font-family: \'times new roman\', times, serif;\">Tiết kiệm ng&acirc;n s&aacute;ch trong việc mua quần &aacute;o Online</span></h2>\r\n<p align=\"center\"><span style=\"font-family: \'times new roman\', times, serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/mua-quan-ao-qua-mang-tiet-kiem.jpg\" alt=\"mua-quan-ao-qua-mang-tiet-kiem\" /></em></span><br /><span style=\"font-family: \'times new roman\', times, serif;\"><em>C&oacute; nhiều c&aacute;ch gi&uacute;p bạn <a href=\"http://canifa.com/blog/tiet-kiem-tien-mua-quan-ao/\" target=\"_blank\">tiết kiệm tiền trong việc mua quần &aacute;o</a>. Quan trọng nhất l&agrave; mua sắm theo mục đ&iacute;ch sử dụng v&agrave; hoạch định tốt chi ti&ecirc;u.</em></span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Với h&igrave;nh thức mua sắm trực tuyến th&igrave; h&atilde;y &aacute;p dụng những kinh nghiệm sau đ&acirc;y:</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Đặt ra giới hạn ng&acirc;n s&aacute;ch</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Để tr&aacute;nh việc chi ti&ecirc;u qu&aacute; mức cho ph&eacute;p, bạn cần phải biết bạn c&oacute; thể chi trả bao nhi&ecirc;u cho việc mua sắm quần &aacute;o Online. H&atilde;y x&aacute;c định lại t&igrave;nh h&igrave;nh t&agrave;i ch&iacute;nh của m&igrave;nh để đặt ra một giới hạn ng&acirc;n s&aacute;ch ph&ugrave; hợp.</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Tham khảo c&ugrave;ng l&uacute;c nhiều trang Web b&aacute;n đồ</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Đ&acirc;y thực sự l&agrave; một thế mạnh khi mua quần &aacute;o Online. C&ugrave;ng một l&uacute;c bạn c&oacute; thể xem gi&aacute; của một chiếc v&aacute;y len tại nhiều cửa h&agrave;ng kh&aacute;c nhau chỉ v&agrave;i c&uacute; nhấp chuột. H&atilde;y tận dụng điều n&agrave;y để so s&aacute;nh gi&aacute; cả giữa c&aacute;c cửa h&agrave;ng. Chọn một Website b&aacute;n quần &aacute;o trực tuyến uy t&iacute;n, họ sẽ c&oacute; những mục so s&aacute;nh gi&aacute; cả gi&uacute;p bạn. Bạn c&oacute; thể dễ d&agrave;ng nhận ra, c&ugrave;ng một mặt h&agrave;ng, nhưng giữa c&aacute;c Shop sẽ c&oacute; mức gi&aacute; cả kh&aacute;c nhau.</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">C&aacute;ch thức mua h&agrave;ng Online</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">T&igrave;m kiềm bằng Google l&agrave; c&aacute;ch dễ nhất để t&igrave;m ra loại quần &aacute;o bạn muốn mua. C&aacute;c Website sẽ hiện ra, bạn chỉ việc v&agrave;o chọn, rồi cho sản phẩm ưng &yacute; v&agrave;o giỏ h&agrave;ng, chuyển tiền qua thẻ rồi nhận h&agrave;ng hoặc ngồi đợi nhận h&agrave;ng sau đ&oacute; mới trả tiền.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Nhưng c&aacute;ch nhanh nhất l&agrave; nhận đăng k&yacute; Email của c&aacute;c website b&aacute;n h&agrave;ng trực tuyến bạn hay mua h&agrave;ng. Bạn sẽ dễ d&agrave;ng cập nhật được tin tức, sản phẩm quần &aacute;o mới ra mắt, <a href=\"http://canifa.com/promotion/\" target=\"_blank\">chương tr&igrave;nh khuyến mại</a>, đợt Sale off...từ nh&atilde;n h&agrave;ng thời trang m&agrave; m&igrave;nh y&ecirc;u th&iacute;ch. Khi c&oacute; nhu cầu, bạn chỉ cần t&igrave;m đến Website đ&oacute; v&agrave; chọn mua h&agrave;ng.</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Mua quần &aacute;o b&aacute;n bu&ocirc;n</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Kh&ocirc;ng nhiều nhưng cũng kh&ocirc;ng phải kh&ocirc;ng c&oacute; tr&ecirc;n c&aacute;c trang Web b&aacute;n h&agrave;ng trực tuyến.&nbsp; Mua quần &aacute;o b&aacute;n bu&ocirc;n tr&ecirc;n trực tuyến bao giờ cũng được gi&aacute; rẻ hơn so với việc mua lẻ. Tuy nhi&ecirc;n, bạn sẽ phải rủ th&ecirc;m bạn b&egrave;, người th&acirc;n để mua chung. Bạn cũng c&oacute; thể mua một số mặt h&agrave;ng với số lượng lớn như tất, vớ, đồ l&oacute;t để mua được gi&aacute; b&aacute;n bu&ocirc;n.</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Kiểm tra ph&iacute; vận chuyển</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Bạn phải xem trước chi ph&iacute; vận chuyển trước khi quyết định nhấn n&uacute;t &ldquo;Cho v&agrave;o giỏ h&agrave;ng&rdquo;. Th&ocirc;ng thường, c&aacute;c h&atilde;ng thời trang nổi tiếng sẽ c&oacute; <a href=\"http://canifa.com/van-chuyen.html\" target=\"_blank\">ch&iacute;nh s&aacute;ch vận chuyển</a> v&agrave; giao h&agrave;ng r&otilde; r&agrave;ng, bạn sẽ kh&ocirc;ng phải lo lắng tới việc ph&aacute;t sinh th&ecirc;m ph&iacute;, nhưng đối với c&aacute;c shop nhỏ, chưa c&oacute; thương hiệu, t&ecirc;n tuổi, h&atilde;y chắc chắn rằng người giao h&agrave;ng kh&ocirc;ng v&ograve;i th&ecirc;m ph&iacute; vận chuyển.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Đ&ocirc;i khi bạn cũng phải c&acirc;n nhắc để mua một m&oacute;n đồ được Sale với chi ph&iacute; vận chuyển. Nếu chi ph&iacute; vận chuyển qu&aacute; lớn so với gi&aacute; trị của m&oacute;n đồ, bạn sẽ phải suy nghĩ kỹ hơn tới việc mua h&agrave;ng.</span></p>\r\n<h2><span style=\"font-family: \'times new roman\', times, serif;\">Mua quần &aacute;o Online: Để kh&ocirc;ng &ldquo;tiền mất, tật mang&rdquo;</span></h2>\r\n<p align=\"center\"><span style=\"font-family: \'times new roman\', times, serif;\"><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/mua-quan-ao-qua-mang-chieu-tro.jpg\" alt=\"mua-quan-ao-qua-mang-chieu-tro\" /></span><br /><span style=\"font-family: \'times new roman\', times, serif;\"><em>Mua sắm Online lu&ocirc;n c&oacute; những cạm bẫy r&igrave;nh rập, do đ&oacute;, người trong cuộc cần phải tinh &yacute; để tr&aacute;nh &ldquo;tiền mất, tật mang&rdquo;</em></span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">C&oacute; kh&ocirc;ng &iacute;t <a href=\"http://ione.vnexpress.net/tin-tuc/thoi-trang/mot/loat-anh-tham-hoa-mua-do-online-do-nhin-duoc-cuoi-3358155.html\" target=\"_blank\" rel=\"nofollow\">trường hợp dở kh&oacute;c dở cười li&ecirc;n quan đến việc mua h&agrave;ng thời trang qua mạng</a>. L&yacute; do th&igrave; c&oacute; nhiều v&agrave; để kh&ocirc;ng trở th&agrave;nh &ldquo;nạn nh&acirc;n&rdquo; của t&igrave;nh trạng n&agrave;y, bạn h&atilde;y nắm l&ograve;ng những quy tắc sau đ&acirc;y:</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Mua từ người b&aacute;n h&agrave;ng tin cậy</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">T&ocirc;i khuy&ecirc;n bạn chỉ n&ecirc;n mua h&agrave;ng từ Website ch&iacute;nh thức của c&aacute;c thương hiệu m&agrave; m&igrave;nh y&ecirc;u th&iacute;ch.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Nếu bạn mua từ c&aacute;c cửa h&agrave;ng nhỏ hơn hoặc người b&aacute;n h&agrave;ng c&aacute; nh&acirc;n, h&atilde;y lựa chọn phương thức thanh to&aacute;n th&ocirc;ng minh bằng chuyển khoản hoặc thanh to&aacute;n sau khi nhận được h&agrave;ng, tr&aacute;nh thanh to&aacute;n qua mạng.</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Đọc comment v&agrave; c&aacute;c đ&aacute;nh gi&aacute;, &yacute; kiến mua h&agrave;ng</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Bạn n&ecirc;n đọc hết c&aacute;c comment b&ecirc;n dưới sản phẩm để biết được phản hồi của c&aacute;c kh&aacute;ch h&agrave;ng kh&aacute;c về sản phẩm m&agrave; bạn định mua. Đừng qu&aacute; tin v&agrave;o những đ&aacute;nh gi&aacute; 5 sao v&igrave; c&oacute; thể đ&oacute; l&agrave; do người b&aacute;n h&agrave;ng tự tạo ra.</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Nhận biết c&aacute;c trang mua b&aacute;n trực tuyến giả</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Đ&ocirc;i khi, c&aacute;c thương hiệu nổi tiếng rất dễ bị l&agrave;m giả trang Web. V&igrave; vậy, bạn phải nắm vững được c&aacute;c dấu hiệu nhận biết của một Website giả bằng c&aacute;ch kiểm tra kỹ t&ecirc;n miền, logo, h&igrave;nh ảnh, nội dung b&ecirc;n trong&hellip;của họ.</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Kh&ocirc;ng đưa ra hết c&aacute;c th&ocirc;ng tin c&aacute; nh&acirc;n</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">T&ecirc;n v&agrave; địa chỉ l&agrave; những thứ cần thiết để bạn mua h&agrave;ng. Nhưng số chứng minh thư hay số t&agrave;i khoản ng&acirc;n h&agrave;ng th&igrave; kh&ocirc;ng nhất thiết phải điền. Nếu cảm thấy nghi ngờ, h&atilde;y hỏi kỹ lại người b&aacute;n h&agrave;ng xem họ cần những th&ocirc;ng tin đ&oacute; để l&agrave;m g&igrave;. Tuyệt đối kh&ocirc;ng cung cấp th&ocirc;ng tin cho họ nếu cảm thấy kh&ocirc;ng thuyết phục, kh&ocirc;ng hợp l&yacute;.</span></p>\r\n<h3><span style=\"font-family: \'times new roman\', times, serif;\">Kiểm tra c&aacute;c ch&iacute;nh s&aacute;ch ho&agrave;n trả trước</span></h3>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\">Trước khi bạn cam kết mua h&agrave;ng, h&atilde;y kiểm tra c&aacute;c ch&iacute;nh s&aacute;ch ho&agrave;n trả trước. Họ c&oacute; cung cấp dịch vụ ho&agrave;n tiền v&agrave; trả lại đồ thuận tiện khi bạn kh&ocirc;ng ưng &yacute; trước m&oacute;n đồ hay kh&ocirc;ng. Ngay cả một người b&aacute;n h&agrave;ng uy t&iacute;n nhưng kh&ocirc;ng c&oacute; c&aacute;c ch&iacute;nh s&aacute;ch ho&agrave;n trả r&otilde; r&agrave;ng, th&igrave; bạn vẫn bị bất lợi trước bộ đồ m&igrave;nh mua về nhưng kh&ocirc;ng d&ugrave;ng được.</span></p>\r\n<p><span style=\"font-family: \'times new roman\', times, serif;\"><em>Tr&ecirc;n đ&acirc;y l&agrave; một v&agrave;i &ldquo;b&iacute; k&iacute;p&rdquo; gi&uacute;p bạn mua quần &aacute;o qua mạng tiện lợi, an to&agrave;n v&agrave; nhanh ch&oacute;ng &nbsp;H&atilde;y l&agrave; người ti&ecirc;u d&ugrave;ng th&ocirc;ng th&aacute;i để tiết kiệm được thời gian, c&ocirc;ng sức v&agrave; tiền bạc, bạn nh&eacute;!</em></span></p>', 'http://localhost/projectbkap/uploads/mua-quan-ao-qua-mang.jpg', '1', '1478143446', '1478153636');
INSERT INTO news VALUES ('4', 'Cách giặt và bảo quản để áo len luôn bền đẹp như mới', 'Đồ len là lựa chọn của nhiều người bởi chúng có nhiều kiểu dáng, dễ mặc và là “trợ thủ” đắc lực để chống lại cái lạnh mùa thu đông. Tuy nhiên, việc giặt và bảo quản các trang phục làm từ len để chúng không bị xù sau khoảng thời gian dài sử dụng lại là một', '<p><span style=\"font-family: arial, helvetica, sans-serif;\">Đồ len l&agrave; lựa chọn của nhiều người bởi ch&uacute;ng c&oacute; nhiều kiểu d&aacute;ng, dễ mặc v&agrave; l&agrave; &ldquo;trợ thủ&rdquo; đắc lực để chống lại c&aacute;i lạnh m&ugrave;a thu đ&ocirc;ng. Tuy nhi&ecirc;n, việc giặt v&agrave; bảo quản c&aacute;c trang phục l&agrave;m từ len để ch&uacute;ng kh&ocirc;ng bị x&ugrave; sau khoảng thời gian d&agrave;i sử dụng lại l&agrave; một vấn đề kh&ocirc;ng dễ ch&uacute;t n&agrave;o.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/cach-giat-bao-quan-ao-len.jpg\" alt=\"cach-giat-bao-quan-ao-len\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>&Aacute;o len vốn được mệnh danh l&agrave; &ldquo;những c&ocirc; n&agrave;ng kh&oacute; t&iacute;nh&rdquo; bởi ch&uacute;ng rất dễ bị hỏng d&aacute;ng nếu như kh&ocirc;ng biết c&aacute;ch giặt bảo quản. (Mua sản phẩm&nbsp;&aacute;o len cho <a href=\"http://canifa.com/ao-len-nam-cate.html\" target=\"_blank\">Nam</a> / <a href=\"http://canifa.com/ao-len-nu-nu-cate.html\" target=\"_blank\">Nữ</a> / <a href=\"http://canifa.com/ao-len-be-trai-cate.html\" target=\"_blank\">B&eacute; trai</a> / <a href=\"http://canifa.com/ao-len-be-gai-cate.html\" target=\"_blank\">B&eacute; g&aacute;i</a> tại CANIFA)</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Một v&agrave;i mẹo nhỏ dưới đ&acirc;y sẽ gi&uacute;p bạn c&oacute; những b&iacute; k&iacute;p &ldquo;giữ d&aacute;ng&rdquo; cho những chiếc &aacute;o len &ldquo;si&ecirc;u độc&rdquo; của ri&ecirc;ng m&igrave;nh.</span></p>\r\n<h2><span style=\"font-family: arial, helvetica, sans-serif;\">Phần 1: C&aacute;ch giặt &aacute;o len kh&ocirc;ng mất d&aacute;ng</span></h2>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&Aacute;o len đ&uacute;ng l&agrave; một \"c&ocirc; n&agrave;ng kh&oacute; t&iacute;nh\". Để giữ &aacute;o bền đẹp, bạn n&ecirc;n <strong>giặt bằng tay với nước ấm kh&ocirc;ng qu&aacute; 30 độ C</strong>. Nếu muốn &aacute;o giữ được độ đ&agrave;n hồi v&agrave; độ s&aacute;ng vốn c&oacute;, bạn n&ecirc;n pha th&ecirc;m một &iacute;t giấm v&agrave;o nước giặt cuối.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Khi <strong>giặt xong tuyệt đối kh&ocirc;ng n&ecirc;n vắt, vặn xoắn</strong> như những chiếc &aacute;o th&ocirc;ng thường m&agrave; phải b&oacute;p từ dầu n&agrave;y đến đầu kia cho bớt nước.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Việc phơi &aacute;o len cũng cần phải ch&uacute; &yacute;: Treo m&oacute;c &aacute;o len l&uacute;c ch&uacute;ng c&ograve;n ướt sẽ l&agrave;m &aacute;o bị chảy d&agrave;i, mất d&aacute;ng. V&igrave; vậy, chỉ n&ecirc;n phơi &aacute;o ở mặt phẳng hoặc cho trong t&uacute;i lưới rồi phơi ở nơi tho&aacute;ng gi&oacute;, tr&aacute;nh &aacute;nh nắng mặt trời gay gắt.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\">C&aacute;ch giặt một số loại &aacute;o len cơ bản</span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">C&oacute; nhiều loại &aacute;o len v&agrave; mỗi loại sẽ c&oacute; những t&iacute;nh chất kh&aacute;c nhau. Do đ&oacute; c&aacute;ch giặt sẽ c&oacute; đ&ocirc;i ch&uacute;t kh&aacute;c biệt. C&ugrave;ng l&agrave;m theo những hướng dẫn sau đ&acirc;y, bạn nh&eacute;!</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>&Aacute;o len mỏng</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/cach-giat-bao-quan-ao-len-ao-len-mong.jpg\" alt=\"cach-giat-bao-quan-ao-len-ao-len-mong\" /></strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Rất th&iacute;ch hợp với dạng thời tiết m&ugrave;a thu đ&ocirc;ng, &aacute;o len mỏng được người ti&ecirc;u d&ugrave;ng &ldquo;săn đ&oacute;n&rdquo; bởi ch&uacute;ng dễ phối đồ. Những chiếc &aacute;o len mỏng, giữ ấm tốt cũng c&oacute; vẻ ngo&agrave;i rất thời trang. Tuy nhi&ecirc;n, sự mỏng manh của trang phục n&agrave;y đ&ograve;i hỏi khổ chủ phải thật ch&uacute; &yacute; trong kh&acirc;u giặt giũ. Tốt nhất, bạn n&ecirc;n giặt loại &aacute;o n&agrave;y bằng tay.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Nếu giặt m&aacute;y th&igrave; n&ecirc;n sắm một c&aacute;i t&uacute;i giặt, đặt sản phẩm v&agrave;o trong v&agrave; chọn tốc độ thấp nhất để đảm bảo cho &aacute;o kh&ocirc;ng bị hỏng.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>&Aacute;o len Chunky</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/cach-giat-bao-quan-ao-len-ao-chunky.jpg\" alt=\"cach-giat-bao-quan-ao-len-ao-chunky\" /></strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Đại diện cho phong c&aacute;ch nổi loạn, ph&aacute; c&aacute;ch, &aacute;o len chunky lu&ocirc;n được c&aacute;c c&ocirc; n&agrave;ng c&aacute; t&iacute;nh lựa chọn để thể hiện cho phong c&aacute;ch thời trang nữ. Tuy nhi&ecirc;n, sản phẩm n&agrave;y cũng rất &ldquo;&otilde;ng ẹo&rdquo; trong kh&acirc;u giặt l&agrave;. Ch&uacute;ng rất dễ bị co lại, l&agrave;m mất d&aacute;ng. V&igrave; vậy, thay v&igrave; &ldquo;h&igrave; hục&rdquo; tự giặt tại nh&agrave;, bạn n&ecirc;n đem em &yacute; đến tiệm giặt kh&ocirc; để đảm bảo được việc sử dụng l&acirc;u d&agrave;i.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>&Aacute;o len l&ocirc;ng d&ecirc; (Cashmere)</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/cach-giat-bao-quan-ao-len-ao-cashmere.jpg\" alt=\"cach-giat-bao-quan-ao-len-ao-cashmere\" /></strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Chinh phục người ti&ecirc;u d&ugrave;ng bằng sự mềm mại, &aacute;o len Cashmere ng&agrave;y c&agrave;ng được nhiều bạn nữ lựa chọn do gi&aacute; th&agrave;nh ng&agrave;y c&agrave;ng &ldquo;mềm&rdquo; v&agrave; tiện lợi hơn trong c&aacute;ch giặt. Với loại &aacute;o n&agrave;y, bạn ho&agrave;n to&agrave;n c&oacute; thể giặt bằng m&aacute;y với chiếc t&uacute;i giặt v&agrave; nước giặt thật nhẹ dịu. Nhưng bạn cũng phải thật ch&uacute; &yacute; trong kh&acirc;u phơi đồ.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Chất liệu len Cashmere cũng &nbsp;rất dễ biến dạng. Bạn kh&ocirc;ng được vắt như c&aacute;c loại quần &aacute;o th&ocirc;ng thường m&agrave; chỉ được thấm cho kh&ocirc; nước. Khi phơi cũng kh&ocirc;ng được treo mắc m&agrave; phải trải &aacute;o l&ecirc;n một mặt phẳng hoặc cho v&agrave;o lưới phơi đồ rồi để nơi tho&aacute;ng kh&iacute; cho ch&uacute;ng kh&ocirc; tự nhi&ecirc;n. Với sự tỉ mỉ từ c&aacute;ch bảo quản đến c&aacute;ch giặt đồ, chắc chắn &aacute;o len Cashmere sẽ giữ được sự mềm mại vốn c&oacute; của sản phẩm trong một thời gian d&agrave;i.</span></p>\r\n<blockquote>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><strong>Bạn c&oacute; biết?</strong></em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Len Cashmere - L&ocirc;ng d&ecirc; v&agrave; Len l&ocirc;ng cừu l&agrave; những chất liệu c&oacute; khả năng giữ nhiệt v&ocirc; c&ugrave;ng tốt, song ch&uacute;ng lại rất dễ &ldquo;xuống m&atilde;&rdquo; nếu việc giặt kh&ocirc;ng được l&agrave;m đ&uacute;ng c&aacute;ch.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Bạn đọc c&oacute; thể tham khảo chi tiết về <a href=\"http://canifa.com/HUONG-DAN-SU-DUNG-BAO-QUAN-LEN-CASHMERE.HTML\" target=\"_blank\">c&aacute;ch giặt &aacute;o len Cashmere</a> v&agrave; <a href=\"http://canifa.com/TANG-XA-PHONG-CHO-SAN-PHAM-LEN-LONG-CUU.HTML\" target=\"_blank\">c&aacute;ch giặt &aacute;o len l&ocirc;ng cừu</a> m&agrave; CANIFA đ&atilde; từng chia sẻ để sản phẩm lu&ocirc;n bền đẹp như mới.</span></p>\r\n</blockquote>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>&Aacute;o len Cardigan</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/cach-giat-bao-quan-ao-len-ao-cardigan.jpg\" alt=\"cach-giat-bao-quan-ao-len-ao-cardigan\" /></strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Để &aacute;o len Cardigan kh&ocirc;ng bị mất d&aacute;ng hay nh&agrave;u nhĩ trong lần mặc tới, bạn phải gấp &aacute;o thật gọn g&agrave;ng, c&agrave;i khuy cẩn thận để trang phục kh&ocirc;ng bị mắc v&agrave;o c&aacute;c m&oacute;n đồ kh&aacute;c trong tủ đồ. C&aacute;ch giặt &aacute;o Cardigan cũng tương tự như c&aacute;c loại len kh&aacute;c. H&atilde;y cố gắng giặt v&agrave; bảo quản đ&uacute;ng c&aacute;ch, bạn sẽ giữ m&atilde;i được &ldquo;tuổi thanh xu&acirc;n&rdquo; cho sản phẩm.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>V&aacute;y len</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/cach-giat-bao-quan-ao-len-vay-len.jpg\" alt=\"cach-giat-bao-quan-ao-len-vay-len\" /></strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">V&ocirc; c&ugrave;ng dễ kết hợp, dễ mặc, ph&ugrave; hợp với hầu hết d&aacute;ng người khiến v&aacute;y len được chọn l&agrave; một trong những loại trang phục được c&aacute;c bạn nữ lựa chọn nhiều nhất. Tuy nhi&ecirc;n, cũng giống như bất kỳ item n&agrave;o l&agrave;m bằng len, v&aacute;y len cũng rất dễ bị d&atilde;o, nhất l&agrave; ở phần &aacute;o (nếu bạn c&oacute; th&oacute;i quen m&oacute;c v&aacute;y len như những loại v&aacute;y th&ocirc;ng thường kh&aacute;c). V&igrave; vậy, mẹo để bảo quan v&aacute;y len, gi&uacute;p ch&uacute;ng giữ được d&aacute;ng như l&uacute;c mới mua l&agrave; tuyệt đối kh&ocirc;ng treo mắc ch&uacute;ng. H&atilde;y gấp thật gọn g&agrave;ng rồi xếp ngay ngắn b&ecirc;n cạnh những item &ldquo;dễ chịu&rdquo;. Đừng để trang phục nằm c&ugrave;ng với c&aacute;c loại quần &aacute;o c&oacute; vải cứng, nhiều đinh m&oacute;c hay c&uacute;c nh&eacute;.</span></p>\r\n<h2><span style=\"font-family: arial, helvetica, sans-serif;\">Phần 2 &ndash; C&aacute;ch bảo quản &aacute;o len &ldquo;mặc trăm lần vẫn bền, đẹp như mới&rdquo;</span></h2>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Bảo quản &aacute;o len đ&uacute;ng c&aacute;ch gi&uacute;p trang phục lu&ocirc;n đẹp như khi mới mua. Sai lầm nhiều người mắc phải nhất l&agrave; treo &aacute;o len, thay v&igrave; gấp gọn g&agrave;ng v&agrave; cất trong tủ.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\">C&aacute;ch treo để &aacute;o len kh&ocirc;ng bị d&atilde;o</span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&Aacute;o len rất dễ bị chảy d&agrave;i, v&igrave; vậy bạn tuyệt đối kh&ocirc;ng được treo l&ecirc;n. H&atilde;y gấp gọn g&agrave;ng v&agrave; bỏ v&agrave;o tủ để giữ d&aacute;ng cho em ấy. Tuy nhi&ecirc;n, gấp như thế n&agrave;o để &aacute;o kh&ocirc;ng bị nhăn, d&aacute;ng chuẩn cũng l&agrave; cả một vấn đề. Bạn c&oacute; để &yacute; những chiếc &aacute;o mới được gấp gọn g&agrave;ng bọc trong t&uacute;i l&uacute;c mới mua chứ?</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>H&atilde;y gấp ch&uacute;ng theo đ&uacute;ng kiểu sau&nbsp;đ&acirc;y:</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Đầu ti&ecirc;n, lật ngược chiếc &aacute;o lại &uacute;p phần ngực xuống, trải rộng hai c&aacute;nh tay &aacute;o ra.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Tiếp theo, gấp c&aacute;nh tay &aacute;o ra sau phần lưng sau đ&oacute; gấp hai m&eacute;p &aacute;o v&agrave;o ch&iacute;nh giữa sao cho cạnh của hai ống tay chạm nhau ở ch&iacute;nh giữa.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Cuối c&ugrave;ng, t&ugrave;y thuộc v&agrave;o độ d&agrave;i của &aacute;o m&agrave; gập phần dưới l&ecirc;n một đến hai lần. Lật ngược &aacute;o lại v&agrave; bạn đ&atilde; gấp được chiếc &aacute;o len đ&uacute;ng c&aacute;ch rồi.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\">L&agrave;m mất vết sờn</span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&Aacute;o len mặc l&acirc;u ng&agrave;y, va chạm nhiều dễ bị sờn v&agrave; l&agrave;m mất độ b&oacute;ng. Để l&agrave;m mất vết sờn n&agrave;y, phục hồi lại &aacute;o len theo trạng th&aacute;i ban đầu, bạn c&oacute; thể đun nước với giấm theo tỉ lệ 1:1 phun l&ecirc;n chỗ bị sờn rồi đem &aacute;o đi phơi.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Tr&ecirc;n đ&acirc;y l&agrave; một số mẹo giặt v&agrave; bảo quản &aacute;o len bền đẹp như mới. H&atilde;y để những chiếc &aacute;o len c&oacute; cơ hội được &ldquo;phục vụ&rdquo; bạn từ m&ugrave;a đ&ocirc;ng n&agrave;y qua m&ugrave;a đ&ocirc;ng kh&aacute;c! Ch&uacute;c bạn th&agrave;nh c&ocirc;ng!</em></span></p>', 'http://localhost/projectbkap/uploads/cach-giat-bao-quan-ao-len.jpg', '1', '1478152953', '1478153388');
INSERT INTO news VALUES ('5', 'Quần Jogger: Mặc đẹp như mơ với 7 cách mix \"siêu độc\"', 'Quần jogger sẽ trở thành xu hướng mới trong năm nay khi các tín đồ thời trang bắt đầu quan tâm tới phong cách đặc biệt của jogger. Không chỉ là trang phục đường phố, Jogger có thể ứng dụng trong nhiều trường hợp khác nhau. Bạn đã biết cách phối đồ cùng qu', '<p><span style=\"font-family: arial, helvetica, sans-serif;\">Quần jogger sẽ trở th&agrave;nh xu hướng mới trong năm nay khi c&aacute;c t&iacute;n đồ thời trang bắt đầu quan t&acirc;m tới phong c&aacute;ch đặc biệt của jogger. Kh&ocirc;ng chỉ l&agrave; trang phục đường phố, Jogger c&oacute; thể ứng dụng trong nhiều trường hợp kh&aacute;c nhau. Bạn đ&atilde; biết c&aacute;ch phối đồ c&ugrave;ng quần jogger chưa? Nếu chưa, h&atilde;y c&ugrave;ng xem một số gợi &yacute; dưới đ&acirc;y nh&eacute;!</span></p>\r\n<h2><span style=\"font-family: arial, helvetica, sans-serif;\">Cẩm nang duy nhất về c&aacute;ch mặc&nbsp;đẹp quần Jogger gi&agrave;nh cho những \"t&iacute;n đồ\" của trang phục n&agrave;y.</span></h2>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Mặc d&ugrave; đ&atilde; xuất hiện từ l&acirc;u, nhưng quần jogger chỉ phổ biến trong một v&agrave;i năm gần đ&acirc;y, n&oacute; xuất hiện như một hiện tượng của trang phục thể thao n&oacute;i ri&ecirc;ng v&agrave; phong c&aacute;ch đường phố n&oacute;i chung.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\">Đầu ti&ecirc;n, bạn cần biết, jogger l&agrave; loại quần g&igrave;?</span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Quần jogger l&agrave; loại quần thể thao được y&ecirc;u th&iacute;ch, nhất l&agrave; những bạn th&iacute;ch chạy bộ. Ch&uacute;ng thường được l&agrave;m bằng vải b&ocirc;ng mềm hay chất liệu nỉ để tạo sự thoải m&aacute;i cho người tập thể dục. Một v&agrave;i năm trước đ&acirc;y, việc mặc quần jogger đi giữa đường phố được coi l&agrave; người kh&ocirc;ng hiểu biết về thời trang bởi mọi người quan niệm ch&uacute;ng kh&ocirc;ng th&iacute;ch hợp khi đi dạo phố.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Thật may mắn, trong một v&agrave;i năm gần đ&acirc;y đ&atilde; c&oacute; một cuộc c&aacute;ch mạng l&agrave;m thay đổi suy nghĩ đ&oacute;!</span></p>\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif;\"><img title=\"brave_spirit_bst-quan-jogger\" src=\"http://canifa.com/media/wysiwyg/banner2016/brave_spirit_bst.jpg\" alt=\"brave_spirit_bst-quan-jogger\" width=\"1054\" height=\"400\" /></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Quần jogger được sử dụng nhiều hơn khi đi tr&ecirc;n đường, thậm ch&iacute; c&oacute; thể d&ugrave;ng để đi học. Nếu bạn đang băn khoăn kh&ocirc;ng biết mua cho m&igrave;nh chiếc quần jogger thật sự thoải m&aacute;i, hợp thời trang th&igrave; c&oacute; thể tham khảo c&aacute;c sản phẩm cho <a href=\"http://canifa.com/bst-brave-spirit-man.html\" target=\"_blank\">Nam</a> v&agrave; <a href=\"http://canifa.com/bst-brave-spirit-woman.html\" target=\"_blank\">Nữ</a> của CANIFA.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Trong qu&aacute; khứ, quần jogger c&oacute; thể th&ugrave;ng th&igrave;nh v&agrave; lỏng lẻo nhưng ng&agrave;y nay, đẻ hợp với xu hướng thời trang v&agrave; phong c&aacute;ch đường phố, quần jogger đ&atilde; được giảm độ rộng ở phần ch&acirc;n. Điều n&agrave;y l&agrave; cho ch&uacute;ng tuyệt vời hơn khi kết hợp với c&aacute;c loại quần &aacute;o kh&aacute;c nhau. Dưới đ&acirc;y sẽ l&agrave; một số &yacute; tưởng để mặc ch&uacute;ng.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\">Phối đồ với quần Jogger: 7 loại &aacute;o c&oacute; sẵn trong tủ đồ để bạn kết hợp trang phục</span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">C&oacute; thể bạn nghĩ ch&uacute;ng chỉ c&oacute; thể kết hợp với một số bộ đồ nhất định bởi ch&uacute;ng qu&aacute; đơn giản nhưng thực ra ch&uacute;ng linh hoạt hơn bạn nghĩ. Sẽ c&oacute; rất nhiều phong c&aacute;ch kh&aacute;c nhau khi kết hợp với quần jogger.</span></p>\r\n<h4><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Kết hợp quần jogger với &aacute;o thun</em></span></h4>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&Aacute;o ph&ocirc;ng l&agrave; m&oacute;n đồ thời trang linh hoạt nhất, c&oacute; thể phối với nhiều đồ kh&aacute;c nhau. Với ưu điểm l&agrave; dễ mặc, quần jogger rất th&iacute;ch hợp để kết hợp với &aacute;o ph&ocirc;ng, tạo n&ecirc;n phong c&aacute;ch đơn giản, gọn g&agrave;ng.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Dễ d&agrave;ng nhất để kết hợp một chiếc &aacute;o ph&ocirc;ng với quần jogger l&agrave; phối một chiếc jogger đen với &aacute;o ph&ocirc;ng trắng, đi c&ugrave;ng đ&ocirc;i gi&agrave;y lười c&oacute; m&agrave;u sắc chủ đạo v&agrave; trắng hoặc đen trắng kết hợp.</span></p>\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/ket-hop-quan-jogger-ao-thun.jpg\" alt=\"ket-hop-quan-jogger-ao-thun\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Nếu muốn c&oacute; c&aacute;i nh&igrave;n bắt mắt hơn, bạn c&oacute; thể chọn quần jogger c&oacute; họa tiết hoặc hoa văn. Nhưng nếu chiếc &aacute;o ph&ocirc;ng của bạn đ&atilde; c&oacute; m&agrave;u sắc nổi bật, h&atilde;y chọn m&agrave;u sắc đơn giản cho chiếc quần jogger. Đừng l&agrave;m ch&uacute;ng trở n&ecirc;n lộn xộn về m&agrave;u sắc. (Sản phẩm tham khảo: <a href=\"http://canifa.com/catalog/product/view/id/83997/s/quan-the-thao-nam-8bp16w001/category/1266/\" target=\"_blank\">Quần jogger nam</a> / <a href=\"http://canifa.com/catalog/product/view/id/84004/s/quan-the-thao-nu-6bp16w003/category/1267/\" target=\"_blank\">Quần jogger nữ</a>)</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">H&atilde;y chắc chắn rằng bạn chọn được chiếc &aacute;o ph&ocirc;ng v&agrave; quần jogger vừa vặn với th&acirc;n h&igrave;nh của m&igrave;nh. Đừng để chiếc jogger chật qu&aacute; đến nỗi b&oacute; s&aacute;t v&agrave;o ch&acirc;n như chiếc quần legging hay qu&aacute; rộng đến nỗi tr&ocirc;ng bạn trở n&ecirc;n th&ugrave;ng th&igrave;nh v&agrave; l&ocirc;i th&ocirc;i.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Chọn một chiếc &aacute;o ph&ocirc;ng chất lượng, l&agrave;m từ 100% cotton với chiếc quần jogger thoải m&aacute;i, bạn sẽ l&agrave;m ghi điểm trong mắt người đối diện. Chọn th&ecirc;m đ&ocirc;i gi&agrave;y da lộn để đi c&ugrave;ng. T&ocirc;i kh&ocirc;ng chắc bạn c&oacute; thể diện bộ trang phục n&agrave;y tới nơi c&ocirc;ng sở, nhưng n&oacute; rất ph&ugrave; hợp để đi dạo phố hoặc tụ tập bạn b&egrave;.</span></p>\r\n<h4><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Phối &aacute;o sơ mi với quần jogger</em></span></h4>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Một chiếc &aacute;o sơ mi c&oacute; thể sử dụng lợi thế của m&igrave;nh trong cả m&ugrave;a h&egrave; hoặc m&ugrave;a đ&ocirc;ng do t&iacute;nh linh hoạt của ch&uacute;ng.</span></p>\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/ket-hop-quan-jogger-ao-so-mi.jpg\" alt=\"ket-hop-quan-jogger-ao-so-mi\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Bạn c&oacute; thể mở c&uacute;c &aacute;o, c&agrave;i hết c&uacute;c &aacute;o l&ecirc;n, sắn tay &aacute;o hoặc thả cả ống tay xuống c&agrave;i c&uacute;c một c&aacute;ch lịch sự. T&ugrave;y c&aacute;ch bạn chọn, nhưng c&aacute;ch kết hợp dễ d&agrave;ng nhất l&agrave; chọn một chiếc sơ mi ngắn tay để kết hợp với quần jogger, c&ugrave;ng với đ&ocirc;i gi&agrave;y lười v&agrave; một số phụ kiện như mũ, k&iacute;nh c&oacute; thể tạo được những phong c&aacute;ch kh&aacute;c nhau&nbsp; bằng việc thay đổi m&agrave;u sắc giữa ch&uacute;ng. (Ảnh: Internet)</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Để tr&ocirc;ng lịch sự hơn, bạn cũng c&oacute; thể chọn chiếc sơ mi d&agrave;i tay c&ugrave;ng gi&agrave;y thể thao hoặc gi&agrave;y da khi kết hợp với quần jogger. Nhưng chất liệu bạn n&ecirc;n chọn cho quần jogger l&agrave; kaki hoặc vải jean. Ch&uacute;ng sẽ mang tới một c&aacute;i nh&igrave;n trẻ trung nhưng cũng rất chuy&ecirc;n nghiệp nếu bạn c&oacute; &yacute; định mặc tới trường học hoặc nơi l&agrave;m việc.</span></p>\r\n<h4><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Quần jogger với &aacute;o kho&aacute;c denim</em></span></h4>\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/ket-hop-quan-jogger-ao-so-mi-denim.jpg\" alt=\"ket-hop-quan-jogger-ao-so-mi-denim\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Đầu tư một chiếc denim chất lượng c&oacute; thể gi&uacute;p bạn tạo được nhiều phong c&aacute;ch kh&aacute;c nhau từ năm n&agrave;y sang năm kh&aacute;c với mọi thời điểm trong năm bởi denim rất dễ phối đồ. Chọn một chiếc quần jogger tối m&agrave;u để phối hợp với &aacute;o denim truyền thống d&agrave;i tới h&ocirc;ng, b&ecirc;n trong l&agrave; chiếc &aacute;o ph&ocirc;ng sẽ gi&uacute;p bạn cực kỳ thoải m&aacute;i, năng động v&agrave; c&aacute; t&iacute;nh</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Nếu kh&ocirc;ng muốn bộ đồ qu&aacute; ảm đạm trong m&ugrave;a đ&ocirc;ng, bạn cũng c&oacute; thể chọn c&aacute;c m&agrave;u sắc trung t&iacute;nh như x&aacute;m, n&acirc;u, v&agrave;ng thổ để kết hợp c&ugrave;ng denim. Th&ecirc;m một đ&ocirc;i gi&agrave;y thể thao, tất cả sẽ trở n&ecirc;n ho&agrave;n hảo.</span></p>\r\n<h4><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Mix quần jogger với &aacute;o hoodie</em></span></h4>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Cũng giống như jogger, &aacute;o hoodie ban đầu l&agrave; m&oacute;n đồ thể thao. Nhưng khi ch&uacute;ng qu&aacute; phổ biến v&agrave; được tiến h&agrave;nh qua một cuộc c&aacute;ch mạng, ch&uacute;ng được sử dụng trong nhiều hơn trong đời sống hằng ng&agrave;y.</span></p>\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img title=\"quan-jogger-hoodie-nam\" src=\"http://canifa.com/media/wysiwyg/Brave/8LT16W003.jpg\" alt=\"quan-jogger-hoodie-nam\" width=\"1800\" height=\"1200\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Sẽ l&agrave; ho&agrave;n hảo nếu kết hợp &aacute;o kho&aacute;c hoodie với một chiếc <a href=\"http://canifa.com/catalog/product/view/id/84021/s/quan-jogger-nam-8bp16w002/category/1266/\" target=\"_blank\">quần jogger nam</a> ch&iacute;nh hiệu.</em></span></p>\r\n<p align=\"center\"><span style=\"font-family: arial, helvetica, sans-serif;\"><img title=\"quan-jogger-hoddie-nu\" src=\"http://canifa.com/media/wysiwyg/Brave/IMG_1592.jpg\" alt=\"quan-jogger-hoddie-nu\" width=\"1200\" height=\"1800\" /></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Chọn một chiếc &aacute;o kho&aacute;c hoodie m&agrave;u s&aacute;ng với <a href=\"http://canifa.com/bst-brave-spirit-woman/quan-the-theo-nu-6bp16w002.html\" target=\"_blank\">quần jogger nữ</a> c&oacute; m&agrave;u trung t&iacute;nh sẽ cho c&aacute;i nh&igrave;n nhẹ nh&agrave;ng tr&ecirc;n cả bộ đồ, kết hợp với đ&ocirc;i gi&agrave;y lười tối m&agrave;u, chiếc mũ lưỡi trai l&agrave; bạn sẽ thấy m&igrave;nh năng động hơn.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Bạn c&oacute; thể chọn những chiếc quần jogger với chất liệu nỉ, b&ocirc;ng để thoải m&aacute;i trong hoạt động. Mặc bộ đồ n&agrave;y để tập thể thao th&ocirc;ng thường hay đi du lịch cũng rất hợp l&yacute;.</span></p>\r\n<h4><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Quần jogger kết hợp c&ugrave;ng &aacute;o kho&aacute;c bomber</em></span></h4>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&Aacute;o bomber đ&atilde; từng l&agrave;m mưa l&agrave;m gi&oacute; trong nhiều năm trước. Đến thời điểm hiện tại n&oacute; kh&ocirc;ng c&ograve;n qu&aacute; hot, nhưng khi kết hợp với jooger, bạn vẫn thấy được một phong c&aacute;ch mới mẻ.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/ket-hop-quan-jogger-ao-khoac-bomber.jpg\" alt=\"ket-hop-quan-jogger-ao-khoac-bomber\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>H&atilde;y nhớ rằng bạn lu&ocirc;n để mở chiếc kh&oacute;a của &aacute;o bomber để khoe chiếc &aacute;o ph&ocirc;ng b&ecirc;n trong nh&eacute;!</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Bạn n&ecirc;n kết hợp quần jogger với c&ugrave;ng m&agrave;u &aacute;o bomber để tạo sự đồng bộ. Th&ecirc;m một chiếc &aacute;o ph&ocirc;ng với m&agrave;u sắc đối lập sẽ l&agrave;m bạn phong c&aacute;ch hơn. V&iacute; dụ, quần v&agrave; &aacute;o đều c&oacute; gam m&agrave;u trung t&iacute;nh, tốt nhất bạn n&ecirc;n chọn chiếc &aacute;o ph&ocirc;ng trắng để l&agrave;m điểm nhấn cho bộ đồ của m&igrave;nh.</span></p>\r\n<h4><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Mix quần jogger với &aacute;o da</em></span></h4>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Mặc d&ugrave; quần jogger thuộc loại quần thể thao, nhưng thử kết hợp với chiếc &aacute;o da, sự đối lập giữa hai m&oacute;n đồ n&agrave;y sẽ tạo ra một phong c&aacute;ch rất mới mẻ v&agrave; thu h&uacute;t.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/ket-hop-quan-jogger-ao-da.jpg\" alt=\"ket-hop-quan-jogger-ao-da\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>C&aacute;ch tốt nhất để l&agrave;m giảm sự đối lập giữa ch&uacute;ng l&agrave; chọn m&agrave;u sắc giống nhau.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Đừng bao giờ chọn một chiếc quần jogger m&agrave;u be để kết hợp với chiếc &aacute;o da tối m&agrave;u. C&aacute;ch an to&agrave;n nhất để ch&uacute;ng kh&ocirc;ng bị lộn xộn m&agrave;u sắc l&agrave; sử dụng quần jogger tối m&agrave;u hoặc c&oacute; những đường kẻ mỏng. H&atilde;y nghĩ rằng bạn đang thay thế quần skinny bằng quần jogger, như vậy sẽ dễ d&agrave;ng hơn cho bạn trong việc chọn đồ.</span></p>\r\n<h4><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Mặc đẹp quần jogger với &aacute;o blazer</em></span></h4>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/ket-hop-quan-jogger-blazer.jpg\" alt=\"ket-hop-quan-jogger-blazer\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Những chiếc &aacute;o blazers cổ điển thường đi k&egrave;m với &aacute;o sơ mi v&agrave; c&agrave; vạt. Nhưng trong một phong c&aacute;ch mới, n&oacute; c&oacute; thể kết hợp với quần jogger v&agrave; &aacute;o phong để c&oacute; c&aacute;i nh&igrave;n trẻ trung hơn. Bạn c&oacute; th&ecirc;t giữ một m&agrave;u sắc tương tự để c&oacute; c&aacute;i nh&igrave;n hợp l&yacute; hơn.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Đừng đụng độ với phong c&aacute;ch thể thao bằng việc chọn chiếc quần b&ocirc;ng hay nỉ mềm mại. Quần jogger vải cứng, d&agrave;y sẽ th&iacute;ch hợp để kết hợp với &aacute;o blazers hơn.</span></p>\r\n<h4><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Đ&ocirc;i gi&agrave;y n&agrave;o ph&ugrave; hợp với quần jogger?</em></span></h4>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Bạn c&oacute; thể ngạc nhi&ecirc;n v&igrave; c&oacute; rất nhiều loại gi&agrave;y d&eacute;p c&oacute; thể kết hợp với quần jogger. Nhưng c&oacute; một số loại sau đ&acirc;y sẽ th&iacute;ch hợp hơn cả.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">C&oacute; thể bạn đ&atilde; đo&aacute;n ra, gi&agrave;y thể thao l&agrave; loại gi&agrave;y th&iacute;ch nhất để kết hợp với quần jogger. Đừng lo đến việc gi&agrave;y thể thao chỉ th&iacute;ch hợp cho việc tập thể dục bởi n&oacute; ng&agrave;y c&agrave;ng trở n&ecirc;n phổ biến trong c&aacute;c c&aacute;ch kết hợp đồ.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Hơn nữa, một đ&ocirc;i gi&agrave;y thể thao kết hợp với jogger c&oacute; thể thuận tiện cho bạn. Bất cứ l&uacute;c n&agrave;o bạn cũng c&oacute; thể chạy bộ tr&ecirc;n đường phố với một bộ đồ thoải m&aacute;i, dễ chịu khi đi gi&agrave;y thể thao v&agrave; mặc quần jogger.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Đ&ocirc;i khi, bạn sẽ kh&ocirc;ng muốn đi tất hoặc vớ. V&agrave; v&igrave; vậy, những chiếc gi&agrave;y lười l&agrave; sự lựa chọn ho&agrave;n hảo. Bạn c&oacute; thể chọn đ&ocirc;i gi&agrave;y lười với chất liệu da lộn sẽ cho c&aacute;i nh&igrave;n lịch sự. H&atilde;y cố gắng chọn m&agrave;u gi&agrave;y c&ugrave;ng với m&agrave;u quần jogger của bạn. Nh&igrave;n tổng thể tr&ocirc;ng ch&uacute;ng sẽ ăn nhập với nhau hơn.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong><em>Lời kết,</em></strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Sẽ kh&ocirc;ng c&oacute; qu&aacute; nhiều quy định, nguy&ecirc;n tắc đối với việc mặc một chiếc quần jogger. Tr&ecirc;n đ&acirc;y chỉ l&agrave; những gợi &yacute; cho bạn để c&oacute; được một phong c&aacute;ch ph&ugrave; hợp với xu hướng chung. Nhưng nếu bạn muốn m&igrave;nh trở n&ecirc;n đặc biệt, đừng ngại thử phối hợp đồ theo phong c&aacute;ch của ri&ecirc;ng bạn. Biết đ&acirc;u, một <a href=\"http://canifa.com/phoi-do-layer-thu-dong.html\" target=\"_blank\">c&aacute;ch phối đồ</a> t&aacute;o bạo sẽ tạo ra một phong c&aacute;ch ăn mặc ấn tượng, trở th&agrave;nh xu hướng trong nay mai th&igrave; sao? C&oacute; thể lắm chứ! V&igrave; vậy, h&atilde;y sắm cho m&igrave;nh v&agrave;i chiếc quần jogger để phối đồ theo c&aacute;ch ri&ecirc;ng của bạn n&agrave;o!</span></p>', 'http://localhost/projectbkap/uploads/brave_spirit_bst.jpg', '1', '1478153583', '1478153583');
INSERT INTO news VALUES ('6', '5 phút chọn đồ mặc nhà làm “tôn da chuẩn dáng” cho phái đẹp', 'Bạn tự tin với chiều cao của mình nhưng lại quá gầy để diện những bộ đồ quyến rũ? Bạn đạt tiêu chuẩn số đo vòng 1 và 3 nhưng vòng 2 lại quá khổ? Những bộ đồ mặc bên ngoài rất dễ lựa chọn vì chúng có nhiều kiểu dáng và dễ chọn theo dáng người. Nhưng còn đồ', '<p><span style=\"font-family: arial, helvetica, sans-serif;\">Bạn tự tin với chiều cao của m&igrave;nh nhưng lại qu&aacute; gầy để diện những bộ đồ quyến rũ? Bạn đạt ti&ecirc;u chuẩn số đo v&ograve;ng 1 v&agrave; 3 nhưng v&ograve;ng 2 lại qu&aacute; khổ? Những bộ đồ mặc b&ecirc;n ngo&agrave;i rất dễ lựa chọn v&igrave; ch&uacute;ng c&oacute; nhiều kiểu d&aacute;ng v&agrave; dễ chọn theo d&aacute;ng người. Nhưng c&ograve;n đồ mặc nh&agrave; th&igrave; sao? Bạn đ&atilde; biết những mẹo chọn đồ mặc nh&agrave; đơn giản, c&oacute; ph&eacute;p thuật thần kỳ h&ocirc; biến người gầy th&agrave;nh tr&ograve;n đầy, người b&eacute;o th&agrave;nh mẫu chuẩn eo, gọn g&agrave;ng chưa? C&ugrave;ng t&igrave;m hiểu những mẹo chọn đồ mặc nh&agrave; đẹp miễn ch&ecirc; dưới đ&acirc;y nh&eacute;!</span></p>\r\n<h2><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Đồ mặc nh&agrave;: Kinh nghiệm lựa chọn trang phục vừa đẹp lại thoải m&aacute;i để mặc từ nh&agrave; ra phố</strong></span></h2>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Trước hết, bạn cần ph&acirc;n biệt được như thế n&agrave;o l&agrave; đồ mặc nh&agrave;, như thế n&agrave;o l&agrave; đồ ngủ. Th&ocirc;ng thường mọi người hay nhầm lẫn giữa 2 kh&aacute;i niệm n&agrave;y dẫn tới bất tiện trong một số ho&agrave;n cảnh như tiếp kh&aacute;ch hay đi ra ngo&agrave;i.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/chon-do-mac-nha-cover.jpg\" alt=\"chon-do-mac-nha-cover\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Đồ mặc nh&agrave; l&agrave; những bộ đồ gi&uacute;p bạn thoải m&aacute;i trong sinh hoạt, kh&ocirc;ng qu&aacute; hở hang để khi c&oacute; kh&aacute;ch tới chơi nh&agrave; bạn vẫn c&oacute; thể tự tin tiếp kh&aacute;ch, c&oacute; lỡ qu&ecirc;n mua mớ rau cũng c&oacute; thể ra ngo&agrave;i mua m&agrave; kh&ocirc;ng thấy ngại. Hiểu ngắn gọn, đồ mặc nh&agrave; l&agrave; bộ đồ mặc ra đường đơn giản nhất. Tại CANIFA, bạn c&oacute; thể mua sắm &ldquo;tẹt ga&rdquo; những mẫu quần &aacute;o thời trang trong d&ograve;ng sản phẩm mặc ở nh&agrave; của ch&uacute;ng t&ocirc;i tại: <a href=\"http://canifa.com/do-mac-nha-nam-cate.html\" target=\"_blank\">Nam</a> / <a href=\"http://canifa.com/do-mac-nha-nu-cate.html\" target=\"_blank\">Nữ</a> / <a href=\"http://canifa.com/do-mac-nha-be-trai-cate.html\" target=\"_blank\">B&eacute; trai</a> / <a href=\"http://canifa.com/do-mac-nha-be-gai-cate.html\" target=\"_blank\">B&eacute; g&aacute;i</a></em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Ngược lại...</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Đồ ngủ như t&ecirc;n goi l&agrave; trang phục được mặc để đi ngủ v&agrave; kh&ocirc;ng c&oacute; một form d&aacute;ng n&agrave;o cụ thể. Chọn trang phục đi ngủ chỉ cần bạn cảm thấy thoải m&aacute;i, dễ chịu l&agrave; được.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Sau khi ph&acirc;n biệt được 2 loại trang phục n&agrave;y rồi th&igrave; bạn h&atilde;y học c&aacute;ch chọn cho m&igrave;nh v&agrave; những th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh những bộ đồ mặc nh&agrave; sao cho ph&ugrave; hợp qua những &ldquo;b&iacute; k&iacute;p&rdquo; được chia sẻ dưới đ&acirc;y nh&eacute;!</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Chọn đồ mặc nh&agrave; cho bạn nữ c&oacute; th&acirc;n h&igrave;nh gầy, mỏng</strong></span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Kh&ocirc;ng c&oacute; da c&oacute; thịt như người ta cũng l&agrave; nỗi niềm của rất nhiều n&agrave;ng. Với c&aacute;c bạn nữ c&oacute; th&acirc;n h&igrave;nh &ldquo;c&ograve; hương&rdquo; nếu kh&ocirc;ng biết c&aacute;ch chọn đồ sẽ l&agrave;m cho người đối diện cảm thấy bạn thiếu sức sống. Vậy mẹo chọn đồ mặc nh&agrave; cho bạn l&agrave; g&igrave;?</span></p>\r\n<h4><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Chọn chất liệu vải</strong></span></h4>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">V&agrave;o m&ugrave;a đ&ocirc;ng, bạn c&oacute; thể đ&aacute;nh lừa thị gi&aacute;c của người đối diện bằng những bộ đồ ấm &aacute;p v&agrave; dầy dặn, nhưng thật kh&oacute; để che dấu d&aacute;ng người mảnh khảnh của bạn với c&aacute;c chất liệu qu&aacute; mềm mỏng như lụa, lanh. Ch&uacute;ng sẽ rủ xuống th&acirc;n h&igrave;nh b&eacute; nhỏ của bạn, tạo cảm gi&aacute;c bạn đang bơi trong &aacute;o.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Chất liệu bạn n&ecirc;n chọn l&agrave; cotton, vải x&ocirc;, đũi, kate mỏng. Những chất liệu n&agrave;y c&oacute; thể thấm h&uacute;t tốt, tho&aacute;ng m&aacute;t m&agrave; vẫn c&oacute; những độ phồng nhất định, gi&uacute;p bạn tự tin khi diện. Th&acirc;n h&igrave;nh nhỏ nhắn, mảnh khảnh của bạn sẽ được che đậy tốt bởi đồ mặc nh&agrave; l&agrave;m từ chất liệu n&agrave;y, đ&aacute;nh lừa thị gi&aacute;c của đối phương rằng th&acirc;n h&igrave;nh của bạn cũng tr&ograve;n đầy, đạt chuẩn đấy chứ.</span></p>\r\n<h4><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Chọn kiểu d&aacute;ng, họa tiết</strong></span></h4>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Nếu rất th&iacute;ch những m&oacute;n đồ mặc nh&agrave; c&oacute; chất liệu rũ mỏng như lụa, bạn n&ecirc;n chọn những bộ trang phục c&oacute; c&aacute;c thiết kế bồng ở phần vai hay nh&uacute;m b&egrave;o, nơ hoa&hellip;Trọng lượng cơ thể tr&ocirc;ng cũng n&acirc;ng l&ecirc;n đ&aacute;ng kể với c&aacute;c mẫu đồ mặc nh&agrave; c&oacute; thiết kế như vậy.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Một lưu &yacute; nữa khi chọn họa tiết cho c&aacute;c bộ đồ mặc nh&agrave;. Tuyệt đối kh&ocirc;ng chọn những bộ đồ c&oacute; kẻ dọc d&agrave;y. Ch&uacute;ng chỉ c&oacute; chức năng &ldquo;d&igrave;m h&agrave;ng&rdquo; bạn m&agrave; th&ocirc;i. Nhưng c&aacute;c họa tiết như kẻ caro bản lớn lại gi&uacute;p bạn &ldquo;tăng c&acirc;n&rdquo; đ&aacute;ng kể trong mắt mọi người đấy.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Chọn trang phục mặc nh&agrave; cho bạn nữ c&oacute; th&acirc;n h&igrave;nh &ldquo;qu&aacute; khổ&rdquo;</strong></span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Hẳn bạn sẽ bị mặc cảm khi đi chọn đồ m&agrave; kh&ocirc;ng c&oacute; lấy một bộ đồ ưng &yacute;. Nhưng rất may, đồ mặc nh&agrave; sẽ c&oacute; nhiều size, gi&uacute;p bạn thoải m&aacute;i khi hoạt động m&agrave; kh&ocirc;ng cần để &yacute; qu&aacute; nhiều.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/bodynamnu-top.jpg\" alt=\"chon-do-mac-nha-hop-dang-nguoi\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Điều quan trọng l&agrave; bạn phải chọn được quần &aacute;o mặc nh&agrave; c&oacute; chất liệu, m&agrave;u sắc, kiểu d&aacute;ng v&agrave; họa tiết ph&ugrave; hợp để che bớt đi khuyết điểm của m&igrave;nh.</em></span></p>\r\n<h4><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Chọn m&agrave;u sắc</strong></span></h4>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Vấn đề của bạn l&agrave; phải l&agrave;m cho người kh&aacute;c cảm thấy m&igrave;nh kh&ocirc;ng qu&aacute; b&eacute;o. C&aacute;c gam m&agrave;u tối sẽ hỗ trợ đắc lực cho bạn trong việc thực hiện mục ti&ecirc;u đ&aacute;nh lừa thị gi&aacute;c. Bạn n&ecirc;n chọn c&aacute;c m&agrave;u như xanh đậm, đen, n&acirc;u&hellip; vừa c&oacute; thể t&ocirc;n l&ecirc;n l&agrave;n da trắng hồng lại gi&uacute;p bạn &ldquo;thon gọn&rdquo; trong mắt người đối diện. Nhưng chờ đ&atilde;, nếu bạn c&oacute; l&agrave;n da ngăm đen, b&aacute;nh mật th&igrave; sao? Vẫn c&oacute; những gam m&agrave;u gi&uacute;p bạn &ldquo;giảm c&acirc;n&rdquo; như đỏ đ&ocirc;, nho, cẩm t&iacute;m&hellip; m&agrave; vẫn t&ocirc;n l&ecirc;n l&agrave;n da khỏe khoắn của bạn.</span></p>\r\n<h4><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Chọn kiểu d&aacute;ng</strong></span></h4>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Cũng kh&ocirc;ng qu&aacute; kh&oacute; để chọn kiểu d&aacute;ng đồ mặc nh&agrave; cho người b&eacute;o. Bạn c&oacute; thể chọn những bộ đồ c&oacute; cổ tim, cổ trễ, cổ tr&ograve;n rộng vừa tho&aacute;i m&aacute;i khi mặc lại tạo được cảm gi&aacute;c phần cổ mảnh mai hơn. Ngo&agrave;i ra, bạn n&ecirc;n chọn những bộ đồ vừa kh&iacute;t với th&acirc;n h&igrave;nh của m&igrave;nh, kh&ocirc;ng qu&aacute; chật để l&agrave;m lộ những ngấn mỡ thừa của m&igrave;nh, nhưng cũng kh&ocirc;ng qu&aacute; rộng để ch&uacute;ng đi &ldquo;tố c&aacute;o&rdquo; chủ nh&acirc;n của ch&uacute;ng.</span></p>\r\n<h2><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Gợi &yacute; một số m&oacute;n đồ mặc nh&agrave; cho bạn</strong></span></h2>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/HelloKitty-topbn.jpg\" alt=\"cac-trang-phuc-mac-nha-dep\" /></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Thời trang mặc nh&agrave; thực sự rất đa dạng về kiểu d&aacute;ng, mẫu m&atilde; v&agrave; chủng loại sản phẩm. Dưới đ&acirc;y l&agrave; một số gợi &yacute; m&agrave; bạn n&ecirc;n tham khảo để bổ sung cho tủ đồ của m&igrave;nh:</span></p>\r\n<h5><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Quần đ&ugrave;i</strong></span></h5>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Loại quần ngắn tr&ecirc;n đầu gối l&agrave; lựa chọn của kh&aacute; nhiều bạn trẻ. T&ugrave;y theo sở th&iacute;ch m&agrave; bạn c&oacute; thể chọn độ ngắn cho ph&ugrave; hợp. Những chiếc quần qu&aacute; ngắn sẽ tạo được sự thoải m&aacute;i nhưng sẽ kh&ocirc;ng lịch sự. Những chiếc quần ngang đ&ugrave;i sẽ th&iacute;ch hợp hơn nếu trong gia đ&igrave;nh c&oacute; người lớn tuổi. Bạn cũng n&ecirc;n cẩn thận với quần đ&ugrave;i cạp chun bởi ch&uacute;ng sẽ b&oacute; s&aacute;t v&agrave;o cơ thể, tr&ocirc;ng như đồ mặc ngủ hơn l&agrave; đồ mặc nh&agrave;.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Mua sản phẩm quần đ&ugrave;i tại CANIFA cho <a href=\"http://canifa.com/quan-dui-nam-sub.html\" target=\"_blank\">Nam</a> / <a href=\"http://canifa.com/quan-dui-nu-sub.html\" target=\"_blank\">Nữ</a> / <a href=\"http://canifa.com/quan-mac-nha-be-trai-sub.html\" target=\"_blank\">B&eacute; trai</a> / <a href=\"http://canifa.com/quan-mac-nha-be-gai-sub.html\" target=\"_blank\">B&eacute; g&aacute;i</a></em></span></p>\r\n<h5><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Quần lửng</strong></span></h5>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Quần dạng n&agrave;y kh&aacute; an to&agrave;n v&igrave; ch&uacute;ng d&agrave;i qua gối đến bắp ch&acirc;n. Bạn c&oacute; thể tự tin chạy ra ngo&agrave;i mua đồ m&agrave; kh&ocirc;ng cần thay quần &aacute;o kh&aacute;c. Với quần dạng n&agrave;y cũng c&oacute; nhiều kiểu d&aacute;ng để bạn lựa chọn.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Mua sản phẩm quần lửng tại CANIFA cho <a href=\"http://canifa.com/quan-lung-nam-cate.html\" target=\"_blank\">Nam</a> / <a href=\"http://canifa.com/quan-lung-nu-cate.html\" target=\"_blank\">Nữ</a></em></span></p>\r\n<h5><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Quần d&agrave;i</strong></span></h5>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Quần d&agrave;i thường được lựa chọn khi m&ugrave;a thu đ&ocirc;ng đến. C&oacute; hai loại quần được mọi người chọn mặc khi ở nh&agrave; l&agrave; quần legging v&agrave; quần jogger. Với quần legging b&oacute; s&aacute;t cơ thể, bạn c&oacute; thể phối với nhiều đồ để mặc ra ngo&agrave;i m&agrave; tr&ocirc;ng vẫn lịch sự, đẹp đẽ v&agrave; t&ocirc;n d&aacute;ng. C&ograve;n với quần jogger rộng r&atilde;i, sẽ gi&uacute;p bạn dễ d&agrave;ng cử động, l&agrave;m việc nh&agrave; một c&aacute;ch thoải m&aacute;i. Một loại quần d&agrave;i kh&aacute;c rất được ưa chuộng v&agrave;o m&ugrave;a đ&ocirc;ng l&agrave; quần nỉ ống d&agrave;i. Bạn c&oacute; thể mua rời hoặc mua theo bộ, rất tiện lợi khi mặc ở nh&agrave; hoặc phải đi đ&acirc;u đ&oacute; trong chốc l&aacute;t.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Mua sản phẩm quần d&agrave;i mặc nh&agrave; CANIFA cho <a href=\"http://canifa.com/quan-dai-mac-nha-nam-sub.html\" target=\"_blank\">Nam</a> / <a href=\"http://canifa.com/quan-dai-mac-nha-nu-sub.html\" target=\"_blank\">Nữ</a>&nbsp;</em></span></p>\r\n<h5><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>V&aacute;y liền mặc nh&agrave;</strong></span></h5>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">V&aacute;y liền mặc nh&agrave; l&agrave; lựa chọn của nhiều chị em khi đ&aacute;p ứng được nhiều ti&ecirc;u ch&iacute;: vừa c&oacute; thể tranh thủ diện khi ở nh&agrave;, cũng rất linh hoạt khi cần đi đ&acirc;u đ&oacute;: đ&oacute;n con, đ&oacute;n kh&aacute;ch đến chơi nh&agrave;. Khi chọn <a href=\"http://canifa.com/vay-mac-nha-nu-sub.html\" target=\"_blank\">v&aacute;y mặc nh&agrave;</a>, bạn kh&ocirc;ng n&ecirc;n chọn v&aacute;y qu&aacute; x&ograve;e bởi vạt v&aacute;y sẽ bị tung l&ecirc;n khi ngồi xuống, cũng kh&ocirc;ng n&ecirc;n chọn đồ chật v&igrave; sẽ g&acirc;y kh&oacute; khi cử động.</span></p>\r\n<h5><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>&Aacute;o thun</strong></span></h5>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Những chiếc &aacute;o ngắn tay hoặc s&aacute;t n&aacute;ch với chất liệu mỏng, nhẹ, dễ thấm mồ h&ocirc;i lu&ocirc;n l&agrave; sự lựa chọn tốt nhất cho bạn v&agrave;o m&ugrave;a h&egrave;. B&ecirc;n cạnh đ&oacute;, &aacute;o thun ba lỗ l&agrave; sản phẩm được lựa chọn nhiều nhất để mặc ở nh&agrave; bởi kiểu d&aacute;ng đơn giản, dễ thấm mồ h&ocirc;i v&agrave; tiện lợi trong sinh hoạt.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Mua &aacute;o thun ba lỗ CANIFA cho <a href=\"http://canifa.com/ao-ba-lo-nam-sub.html\" target=\"_blank\">Nam</a> / <a href=\"http://canifa.com/ao-mac-nha-nu-sub.html\" target=\"_blank\">Nữ</a> / <a href=\"http://canifa.com/ao-ba-lo-be-trai-sub.html\" target=\"_blank\">B&eacute; trai</a> / <a href=\"http://canifa.com/ao-ngan-tay-be-gai-sub.html\" target=\"_blank\">B&eacute; g&aacute;i</a></em></span></p>\r\n<h5><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Đồ bộ</strong></span></h5>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Bạn sẽ kh&ocirc;ng mất qu&aacute; nhiều thời gian để nghĩ tới c&aacute;ch phối đồ sao cho ph&ugrave; hợp. Những bộ đồ lu&ocirc;n l&agrave; lựa chọn dễ d&agrave;ng nhất cho bạn để mặc tại nh&agrave;. Bộ đồ mặc nh&agrave; cũng c&oacute; rất nhiều kiểu d&aacute;ng, thiết kế để bạn lựa chọn, từ &aacute;o s&aacute;t n&aacute;ch với quần lửng tới cả bộ đồ d&agrave;i tay cũng rất ph&ugrave; hợp để bạn chọn đồ mặc nh&agrave;.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Mua c&aacute;c mẫu đồ bộ mặc nh&agrave; đẹp cho <a href=\"http://canifa.com/bo-mac-nha-nu-sub.html\" target=\"_blank\">Nữ</a> / <a href=\"http://canifa.com/bo-do-be-trai-sub.html\" target=\"_blank\">B&eacute; trai</a> / <a href=\"http://canifa.com/bo-do-be-gai-sub.html\" target=\"_blank\">B&eacute; g&aacute;i</a> tại CANIFA</em></span></p>\r\n<h5><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>&Aacute;o kho&aacute;c</strong></span></h5>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">V&agrave;o những h&ocirc;m nhiệt độ xuống thấp, bạn cũng cần c&oacute; chiếc &aacute;o kho&aacute;c để mặc b&ecirc;n ngo&agrave;i chống lạnh. Những chiếc &aacute;o kho&aacute;c len mỏng như cardigan hay &aacute;o kho&aacute;c nỉ c&oacute; mũ v&agrave; kh&oacute;a k&eacute;o dễ d&agrave;ng l&agrave; lựa chọn th&iacute;ch hợp hơn cả.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong><em>Lời kết,</em></strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Một bộ đồ đẹp sẽ gi&uacute;p bạn lu&ocirc;n tự tin trước mọi người, tiện dụng trong sinh hoạt. Vậy qua c&aacute;c mẹo nhỏ b&ecirc;n tr&ecirc;n, bạn đ&atilde; biết c&aacute;ch chọn đồ mặc nh&agrave; ph&ugrave; hợp với v&oacute;c d&aacute;ng của m&igrave;nh chưa? Chất liệu, m&agrave;u sắc, kiểu d&aacute;ng n&agrave;o sẽ gi&uacute;p bạn tự tin ngay cả khi ở nh&agrave;? Trong tủ quần &aacute;o của bạn đ&atilde; những bộ đồ mặc nh&agrave; n&agrave;o rồi? Nếu cảm thấy chưa ổn với những bộ đồ mặc nh&agrave; hiện tại của m&igrave;nh, h&atilde;y tới ngay <a href=\"http://canifa.com/he-thong-cua-hang.html\" target=\"_blank\">cửa h&agrave;ng CANIFA gần nhất</a> để sắm ngay cho m&igrave;nh bộ đồ ph&ugrave; hợp, bạn nh&eacute;!</span></p>', 'http://localhost/projectbkap/uploads/chon-do-mac-nha-cover.jpg', '1', '1478153698', '1478153698');
INSERT INTO news VALUES ('7', 'Cẩm nang kết hợp áo len nam cùng áo sơ mi đầy đủ nhất', 'Mặc một chiếc áo sơ mi kết hợp với áo len sẽ giúp bạn có thêm những phong cách mới trong ngày đông lạnh. Và như bạn biết đấy, “ăn cho mình, mặc cho người”, có được một trang phục đẹp cũng làm cho bạn cảm thấy tự tin hơn rất nhiều.', '<p><span style=\"font-family: arial, helvetica, sans-serif;\">Mặc một chiếc &aacute;o sơ mi kết hợp với &aacute;o len sẽ gi&uacute;p bạn c&oacute; th&ecirc;m những phong c&aacute;ch mới trong ng&agrave;y đ&ocirc;ng lạnh. V&agrave; như bạn biết đấy, &ldquo;ăn cho m&igrave;nh, mặc cho người&rdquo;, c&oacute; được một trang phục đẹp cũng l&agrave;m cho bạn cảm thấy tự tin hơn rất nhiều.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Sự kết hợp giữa sơ mi v&agrave; &aacute;o len cũng rất đa năng. N&oacute; ph&ugrave; hợp với tất cả mọi người trong mọi ho&agrave;n cảnh. D&ugrave; l&agrave; d&acirc;n văn ph&ograve;ng tại nơi l&agrave;m việc hay c&ocirc;ng vi&ecirc;n, hay sinh vi&ecirc;n trong trường học hay trong qu&aacute;n bar...chỉ cần bổ sung th&ecirc;m cho set đồ với một số trang phục hoặc phụ kiện kh&aacute;c l&agrave; bạn đ&atilde; c&oacute; những phong c&aacute;ch ho&agrave;n to&agrave;n mới. Set đồ sơ mi &ndash; &aacute;o len &ndash; quần &acirc;u &ndash; gi&agrave;y da sẽ ph&ugrave; hợp với phong c&aacute;ch <a href=\"http://canifa.com/thoi-trang-cong-so-nam.html\" target=\"_blank\">thời trang nam c&ocirc;ng sở</a> vốn lu&ocirc;n cần sự lịch sự. Nhưng thay quần &acirc;u bằng quần jeans, gi&agrave;y da th&agrave;nh gi&agrave;y thể thao l&agrave; bạn đ&atilde; c&oacute; một bộ đồ để đi dạo trong c&ocirc;ng vi&ecirc;n một c&aacute;ch thoải m&aacute;i.</span></p>\r\n<h2><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>C&aacute;ch kết hợp &aacute;o len nam với &aacute;o sơ mi gi&uacute;p t&ocirc;i mặc đẹp mỗi ng&agrave;y</strong></span></h2>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Kh&ocirc;ng những ph&ugrave; hợp với mọi ho&agrave;n cảnh, khi mặc &aacute;o sơ mi v&agrave; &aacute;o len c&ograve;n rất tiện dụng khi thay đổi thời tiết. Chỉ cần mặc th&ecirc;m chiếc &aacute;o kho&aacute;c hoặc &aacute;o vest trong thời tiết lạnh gi&aacute; l&agrave; bạn sẽ kh&ocirc;ng c&ograve;n lo tới việc thời tiết c&oacute; thể ảnh hưởng đến sức khỏe của m&igrave;nh.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/mix-ao-len-ao-so-mi-nam.jpg\" alt=\"mix-ao-len-ao-so-mi-nam\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Nếu &aacute;o thun v&agrave; quần jeans l&agrave; những trang phục &ldquo;phải mặc&rdquo; trong m&ugrave;a xu&acirc;n h&egrave; th&igrave; khi m&ugrave;a thu đ&ocirc;ng tới, &aacute;o sơ mi phối c&ugrave;ng với &aacute;o len l&agrave; phong c&aacute;ch ăn mặc ho&agrave;n hảo, gi&uacute;p c&aacute;c ch&agrave;ng trai giữ ấm v&agrave; trở l&ecirc;n cuốn h&uacute;t hơn. Mặc d&ugrave; c&oacute; nhiều loại, song cả 2 trang phục n&agrave;y lại rất dễ kết hợp với nhau. Để tạo được những set đồ ấn tượng th&igrave; bạn phải chọn được cho m&igrave;nh những trang phục ph&ugrave; hợp nhất. Mua sản phẩm <a href=\"http://canifa.com/ao-so-mi-nam-cate.html\" target=\"_blank\">&aacute;o sơ mi nam</a> v&agrave; <a href=\"http://canifa.com/ao-len-nam-cate.html\" target=\"_blank\">&aacute;o len nam</a> thời trang, phong c&aacute;ch nhất tại đ&acirc;y.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">V&igrave; sự tiện dụng v&agrave; linh hoạt như vậy, bạn cần biết c&aacute;ch mix sơ mi với &aacute;o len để c&oacute; được c&aacute;i nh&igrave;n ho&agrave;n hảo trước mắt mọi người.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Chọn size vừa vặn với v&oacute;c đ&aacute;ng cơ thể</strong></span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Điều quan trọng nhất khi chọn quần &aacute;o l&agrave; ch&uacute;ng phải vừa vặn với th&acirc;n h&igrave;nh của bạn. Kh&ocirc;ng chọn &aacute;o qu&aacute; chật v&igrave; sẽ l&agrave;m bạn cảm thấy kh&oacute; vận động, qu&aacute; rộng sẽ l&agrave;m mất t&iacute;nh thời trang v&agrave; vướng v&iacute;u trong qu&aacute; tr&igrave;nh hoạt động. Một bộ quần &aacute;o vừa vặn với bạn sẽ gi&uacute;p bạn thoải m&aacute;i trong mọi hoạt động.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Đối với &aacute;o len v&agrave; &aacute;o sơ mi, ch&uacute;ng vừa vặn với bạn nếu hai c&aacute;nh tay v&agrave; vai lu&ocirc;n &ocirc;m s&aacute;t v&agrave;o cơ thể bạn một c&aacute;ch nhẹ nh&agrave;ng. Cổ tay &aacute;o đủ chật để khi bạn đẩy l&ecirc;n c&aacute;nh tay, ch&uacute;ng sẽ ở tr&ecirc;n c&aacute;nh tay thay v&igrave; trượt xuống. Nếu mua một chiếc &aacute;o len để mặc với &aacute;o sơ mi, h&atilde;y mua loại &aacute;o c&oacute; t&iacute;nh đ&agrave;n hồi cao, kh&oacute; bị d&atilde;o để bạn lu&ocirc;n mặc đẹp với chiếc &aacute;o đ&oacute;. Đối với &aacute;o sơ mi, n&oacute; vừa vặn nếu như chiều d&agrave;i vai &aacute;o d&agrave;i bằng vai của bạn. Chiếc &aacute;o phải đủ chật để &ocirc;m s&aacute;t v&agrave;o cơ thể, nhưng cũng đủ rộng để bạn dễ d&agrave;ng hoạt động, v&iacute; dụ như n&acirc;ng c&aacute;nh tay l&ecirc;n chẳng hạn.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Khi mua quần &aacute;o Online, nếu vẫn c&ograve;n băn khoăn chưa biết v&oacute;c d&aacute;ng hiện tại của m&igrave;nh sẽ mặc cỡ n&agrave;o th&igrave; bảng <a href=\"http://canifa.com/huong-dan-chon-size.html/\" target=\"_blank\">hướng dẫn chọn size</a> của CANIFA sau đ&acirc;y sẽ l&agrave; &ldquo;cứu c&aacute;nh&rdquo; cho bạn. Đừng bỏ n&oacute; nếu muốn bạn muốn mua được những trang phục vừa vặn với cơ thể.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Phần cổ &aacute;o</strong></span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Phổ biến nhất trong c&aacute;c thiết kế cổ &aacute;o len l&agrave; loại cổ tr&ograve;n v&agrave; cổ tim. Thường &aacute;o len cổ tr&ograve;n sẽ được thiết kế may l&ecirc;n s&aacute;t ph&iacute;a cổ. V&igrave; vậy, cũng sẽ c&oacute; những nguy&ecirc;n tắc bắt buộc cho hai loại &aacute;o n&agrave;y.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/mix-ao-len-ao-so-mi-nam-kieu.jpg\" alt=\"mix-ao-len-ao-so-mi-nam-kieu\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>&Aacute;o len c&oacute; nhiều kiểu d&aacute;ng v&agrave; mẫu m&atilde;. Song, trang phục m&agrave; c&oacute; thể kết hợp đẹp nhất với &aacute;o sơ mi l&agrave; loại <a href=\"http://canifa.com/ao-len-co-tron-nam-sub.html\" target=\"_blank\">&aacute;o len nam cổ tr&ograve;n</a> v&agrave; <a href=\"http://canifa.com/ao-len-co-tim-nam-sub.html\" target=\"_blank\">&aacute;o len nam cổ tim</a>. Những kiểu &aacute;o len n&agrave;y dễ mua, dễ mặc v&agrave; quan trọng nhất l&agrave; ch&uacute;ng rất &ldquo;dễ t&iacute;nh&rdquo; khi mix đồ.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Nếu phối &aacute;o sơ mi với &aacute;o len cổ tr&ograve;n, bạn n&ecirc;n th&aacute;o c&uacute;c tr&ecirc;n c&ugrave;ng của chiếc sơ mi, bẻ cổ &aacute;o sơ mi ra ph&iacute;a ngo&agrave;i &aacute;o len.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">C&ograve;n nếu mặc &aacute;o sơ mi với &aacute;o len cổ chữ V, h&atilde;y c&agrave;i hết c&uacute;c &aacute;o, thậm ch&iacute; c&oacute; thể thắt một chiếc c&agrave; vạt để tr&ocirc;ng gọn g&agrave;ng hơn. Lưu &yacute; khi thắt c&agrave; vạt, bạn phải để ch&uacute;ng b&ecirc;n trong chiếc &aacute;o len nh&eacute;! Điều n&agrave;y sẽ l&agrave;m bạn tr&ocirc;ng lịch l&atilde;m hơn, c&ograve;n nếu c&agrave; vạt d&agrave;i hơn &aacute;o len của bạn, tốt nhất bạn kh&ocirc;ng n&ecirc;n thắt.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Kh&ocirc;ng nhất thiết phải bẻ cổ &aacute;o ra ngo&agrave;i &aacute;o len, nhưng đối với những chiếc &aacute;o sơ mi c&oacute; đ&iacute;nh họa tiết ở tr&ecirc;n cổ &aacute;o, bạn n&ecirc;n bẻ ch&uacute;ng ra ngo&agrave;i. Những họa tiết n&agrave;y c&oacute; thể l&agrave;m hỏng chiếc &aacute;o len nếu như ch&uacute;ng bị mắc v&agrave; len.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Lưu &yacute;: H&atilde;y chọn cho m&igrave;nh những chiếc &aacute;o len ph&ugrave; hợp với khu&ocirc;n mặt. Bạn nam c&oacute; khu&ocirc;n mặt đầy đặn sẽ th&iacute;ch hợp hơn trong chiếc &aacute;o len cổ chữ V, c&ograve;n những chiếc &aacute;o len cổ tr&ograve;n truyền thống sẽ ph&ugrave; hợp hơn với c&aacute;c bạn c&oacute; khu&ocirc;n mặt d&agrave;i, nhỏ.</em></span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Sơ vin hay kh&ocirc;ng sơ vin?</strong></span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Điều thứ ba bạn cần biết khi lựa chọn <a href=\"http://canifa.com/thoi-trang-thu-dong.html\" target=\"_blank\">phong c&aacute;ch thời c&aacute;ch thu đ&ocirc;ng</a> n&agrave;y l&agrave; n&ecirc;n hay kh&ocirc;ng n&ecirc;n &ldquo;sơ vin&rdquo; &aacute;o sơ mi v&agrave; &aacute;o len.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/mix-ao-len-ao-so-mi-nam-so-vin.jpg\" alt=\"mix-ao-len-ao-so-mi-nam-so-vin\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Ph&aacute;i mạnh c&oacute; thể \"sơ vin\" chiếc &aacute;o sơ mi b&ecirc;n trong hoặc để bu&ocirc;ng tự nhi&ecirc;n t&ugrave;y sở th&iacute;ch. Mỗi kiểu mix lại mang tới cho người mặc một n&eacute;t c&aacute; t&iacute;nh ri&ecirc;ng, khi th&igrave; lịch l&atilde;m, l&uacute;c lại rất c&aacute; t&iacute;nh.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Một nguy&ecirc;n tắc bất dịch, bạn sẽ kh&ocirc;ng bao giờ được &ldquo;sơ vin&rdquo; &aacute;o len nếu như kh&ocirc;ng muốn l&agrave;m tr&ograve; cười tại nơi c&ocirc;ng cộng.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Tuy nhi&ecirc;n, với &aacute;o sơ mi, bạn c&oacute; thể chọn c&aacute;ch &ldquo;sơ vin&rdquo; hoặc kh&ocirc;ng. Nếu tới những nơi trang nghi&ecirc;m v&agrave; lịch sự, bạn n&ecirc;n sơ vin để tr&ocirc;ng lịch l&atilde;m hơn. C&ograve;n nếu chỉ đi dạo tr&ecirc;n b&atilde;i biển hoặc đi chơi trong c&ocirc;ng vi&ecirc;n, bạn c&oacute; thể thả &aacute;o ra để cảm thấy thoải m&aacute;i.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Một lưu &yacute; đặc biệt quan trọng kh&aacute;c l&agrave;: Nếu bạn đ&atilde; sơ vin th&igrave; h&atilde;y đảm bảo chiếc &aacute;o sơ mi nằm gọn g&agrave;ng trong quần chứ kh&ocirc;ng phải c&ograve;n một vạt &aacute;o bị để lại ph&iacute;a ngo&agrave;i. Nếu điều đ&oacute; xảy ra sẽ l&agrave;m bạn tr&ocirc;ng thật l&ocirc;i th&ocirc;i. Bạn kh&ocirc;ng muốn như vậy, đ&uacute;ng kh&ocirc;ng?</span></p>\r\n<h2><span style=\"font-family: arial, helvetica, sans-serif;\">C&aacute;ch phối hợp m&agrave;u sắc v&agrave; họa tiết</span></h2>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Cuối c&ugrave;ng, nhưng kh&ocirc;ng k&eacute;m phần quan trọng trong <a href=\"http://canifa.com/phoi-do-layer-thu-dong.html\" target=\"_blank\">c&aacute;ch phối đồ</a> n&agrave;y l&agrave; kết hợp m&agrave;u sắc v&agrave; họa tiết giữa &aacute;o len v&agrave; &aacute;o sơ mi sao cho ph&ugrave; hợp, tạo n&ecirc;n một c&aacute;i nh&igrave;n tổng thể h&agrave;i h&ograve;a v&agrave; đẹp mắt.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Thứ nhất, về m&agrave;u sắc</strong></span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Chọn m&agrave;u sắc trung t&iacute;nh v&agrave; kh&ocirc;ng trung t&iacute;nh</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/mix-ao-len-ao-so-mi-nam-tron.jpg\" alt=\"mix-ao-len-ao-so-mi-nam-tron\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Nếu bạn kh&ocirc;ng qu&aacute; r&agrave;nh về sự kết hợp m&agrave;u sắc, bạn c&oacute; thể chọn mua c&aacute;c mẫu &aacute;o len c&oacute; m&agrave;u sắc trung t&iacute;nh. Trong thời trang, việc sử dụng những chiếc &aacute;o c&oacute; m&agrave;u sắc trung t&iacute;nh sẽ gi&uacute;p bạn dễ d&agrave;ng phối với c&aacute;c loại quần &aacute;o kh&aacute;c.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Những m&agrave;u trung t&iacute;nh đ&oacute; l&agrave; đen, n&acirc;u, x&aacute;m, trắng ng&agrave;, xanh đậm. Những m&agrave;u n&agrave;y c&oacute; thể kết hợp với nhau v&agrave; cũng rất dễ d&agrave;ng để &ldquo;ăn cặp&rdquo; với c&aacute;c m&agrave;u sắc sặc sỡ kh&aacute;c. V&igrave; vậy, lựa chọn một chiếc &aacute;o len trung t&iacute;nh, bạn c&oacute; thể dễ d&agrave;ng kết hợp với chiếc &aacute;o sơ mi c&oacute; m&agrave;u sắc nổi bật như: t&iacute;m, v&agrave;ng, hồng&hellip; Sự phối hợp giữa m&agrave;u trung t&iacute;nh v&agrave; m&agrave;u kh&ocirc;ng trung t&iacute;nh sẽ lu&ocirc;n l&agrave; một sự lựa chọn an to&agrave;n.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/mix-ao-len-ao-so-mi-nam-mau-sac.jpg\" alt=\"mix-ao-len-ao-so-mi-nam-mau-sac\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Bạn c&oacute; thể chọn mặc chiếc &aacute;o sơ mi v&agrave; &aacute;o len c&ugrave;ng m&agrave;u, nhưng sự kết hợp n&agrave;y đ&ograve;i hỏi phải tinh tế. Nếu bạn chọn một chiếc &aacute;o sơ mi m&agrave;u t&iacute;m đậm th&igrave; h&atilde;y chọn mua chiếc &aacute;o len m&agrave;u t&iacute;m nhạt. Như vậy bạn tr&ocirc;ng sẽ lịch l&atilde;m hơn, người r&agrave;nh về thời trang sẽ thấy đ&acirc;y l&agrave; một c&aacute;ch phối hợp đầy tinh tế.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Phối m&agrave;u</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Nếu muốn trang phục của m&igrave;nh c&oacute; những m&agrave;u sắc bắt mắt, tạo cảm gi&aacute;c vui tươi khi mặc, bạn cũng c&oacute; thể chọn c&aacute;ch kết hợp hai m&agrave;u đối lập. V&iacute; dụ như m&agrave;u xanh l&aacute; v&agrave; m&agrave;u cam, m&agrave;u v&agrave;ng v&agrave; m&agrave;u t&iacute;m. Nhưng n&ecirc;n nhớ, c&aacute;ch phối m&agrave;u sắc n&agrave;y phải l&agrave; những m&agrave;u sắc đối lập nhau nh&eacute;! C&aacute;ch phối n&agrave;y sẽ đem đến sự t&aacute;o bạo trong phong c&aacute;ch thời trang của bạn, bằng sự đối lập, ch&uacute;ng cũng sẽ tự l&agrave;m nổi bật nhau.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Thứ hai, về họa tiết</strong></span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Họa tiết v&agrave; m&agrave;u trung t&iacute;nh</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">C&aacute;ch dễ nhất để phối một chiếc &aacute;o len c&oacute; họa tiết l&agrave; chọn một chiếc &aacute;o sơ mi c&oacute; m&agrave;u trung t&iacute;nh. Tuy nhi&ecirc;n, c&aacute;ch phối n&agrave;y đ&atilde; trở n&ecirc;n qu&aacute; phổ biến v&agrave; g&acirc;y ra sự nh&agrave;m ch&aacute;n. Nếu bạn muốn m&igrave;nh c&oacute; phong c&aacute;ch thời trang nổi bật hơn, h&atilde;y tham khảo những c&aacute;ch phối họa tiết tiếp theo.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/mix-ao-len-ao-so-mi-nam-dong-mau.jpg\" alt=\"mix-ao-len-ao-so-mi-nam-dong-mau\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Nếu c&oacute; một chiếc &aacute;o sơ mi với m&agrave;u sắc sặc sỡ, bạn n&ecirc;n chọn những chiếc &aacute;o len c&oacute; họa tiết để mặc. V&agrave; ngược lại, nếu &aacute;o sơ mi c&oacute; họa tiết kẻ sọc hay caro, h&atilde;y chọn chiếc &aacute;o len trơn m&agrave;u để l&agrave;m nổi bật chiếc sơ mi.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Kết hợp c&aacute;c họa tiết</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Nếu bạn muốn kết hợp nhiều họa tiết với nhau, bạn lu&ocirc;n phải đảm bảo rằng ch&uacute;ng c&oacute; k&iacute;ch cỡ về họa tiết kh&aacute;c nhau. V&iacute; dụ như kết hợp sơ mi chấm bi với kẻ ca r&ocirc; chẳng hạn.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Một chiếc &aacute;o len kết hợp với sơ mi l&agrave; một điều tuyệt vời. Nắm bắt được những quy tắc kết hợp sẽ cho bạn một phong c&aacute;ch <a href=\"http://canifa.com/nam.html\" target=\"_blank\">thời trang nam</a> s&agrave;nh điệu, tự tin sải bước tới bất cứ nơi đ&acirc;u. Những quy tắc tr&ecirc;n chỉ mang t&iacute;nh chất tham khảo, nếu muốn bứt ph&aacute; v&agrave; s&aacute;ng tạo th&ecirc;m, h&atilde;y mạnh dạn thể hiện, c&oacute; thể những phong c&aacute;ch mới lạ sẽ l&agrave;m nổi bật bạn giữa đ&aacute;m đ&ocirc;ng đấy!</em></span></p>', 'http://localhost/projectbkap/uploads/mix-ao-len-ao-so-mi-nam.jpg', '1', '1478153812', '1478153812');
INSERT INTO news VALUES ('8', 'Lịch lãm nơi công sở', 'Đàn ông công sở với những chiếc sơ mi bí ẩn kích thích như viên socola nhân rượu, tưởng trông thấy, chạm thấy, cảm thấy rồi mà vẫn bị bất ngờ chếnh choáng bởi men say khó cưỡng.  Đàn ông công sở với những chiếc Quần Âu gọn gàng, không phô trương nhưng đậm', '<h1 class=\"blog-page-detail-title\"><span style=\"font-family: arial, helvetica, sans-serif;\">Lịch l&atilde;m nơi c&ocirc;ng sở</span></h1>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Đ&agrave;n &ocirc;ng c&ocirc;ng sở với những chiếc sơ mi b&iacute; ẩn k&iacute;ch th&iacute;ch như vi&ecirc;n socola nh&acirc;n rượu, tưởng tr&ocirc;ng thấy, chạm thấy, cảm thấy rồi m&agrave; vẫn bị bất ngờ chếnh cho&aacute;ng bởi men say kh&oacute; cưỡng.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Đ&agrave;n &ocirc;ng c&ocirc;ng sở với những chiếc Quần &Acirc;u gọn g&agrave;ng, kh&ocirc;ng ph&ocirc; trương nhưng đậm say nồng n&agrave;n.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Đ&agrave;n &ocirc;ng c&ocirc;ng sở với Blazer, kh&ocirc;ng đơn giản l&agrave; đẹp, đằng sau đ&oacute; c&ograve;n l&agrave; c&acirc;u chuyện d&agrave;i về kh&iacute; chất đ&agrave;n &ocirc;ng.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Nếu bạn l&agrave; những t&iacute;n đồ thời trang m&ecirc; mẩn sự tinh tế lịch thiệp, h&atilde;y c&ugrave;ng kh&aacute;m ph&aacute; BST Lịch l&atilde;m nơi c&ocirc;ng sở d&agrave;nh cho ph&aacute;i mạnh của CANIFA!</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><a href=\"http://canifa.com/lich-lam-noi-cong-so.html\"><button>Đặt h&agrave;ng ngay</button></a></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><img src=\"http://canifa.com/media/wysiwyg/office-nam/IMG_7359a.jpg\" alt=\"\" /></span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\">&Aacute;o Blazer &amp; Quần &Acirc;u</span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Chiếc &aacute;o Blazer d&aacute;ng &ocirc;m vừa vặn với cơ thể t&ocirc;n l&ecirc;n bờ vai rộng đầy nam t&iacute;nh. Họa tiết kẻ sọc in ch&igrave;m tr&ecirc;n nền vải trung t&iacute;nh khiến chiếc &aacute;o th&ecirc;m phần sang trọng.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Đi k&egrave;m với chiếc Blazer l&agrave; quần &Acirc;u d&aacute;ng &ocirc;m vừa vặn.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Bạn c&oacute; thể lựa chọn bộ suit từ chất liệu vải dạ kẻ mỏng cổ điển hoặc vải tuyt-xi hiện đại.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Vải dạ mỏng với th&agrave;nh phần Polyester-Rayon-Spandex gi&uacute;p tạo form, đứng d&aacute;ng. Tuy chất liệu dạ nhưng vẫn c&oacute; độ co gi&atilde;n nhẹ nh&agrave;ng của sợi chun spandex đảm bảo cử động h&agrave;ng ng&agrave;y thoải m&aacute;i.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Chất liệu Tu&yacute;t-xi trẻ trung hơn với kiểu vải b&oacute;ng hơn c&oacute; th&agrave;nh phần Polyester Rayon gi&uacute;p chống nh&agrave;u v&agrave; đứng d&aacute;ng, tạo phom d&aacute;ng cho người mặc.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\">Sơ mi</span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Kh&aacute;c với chiếc sơ mi kẻ trẻ trung, sơ mi c&ocirc;ng sở lại mang đến sự chỉn chu v&agrave; nghi&ecirc;m t&uacute;c hơn nhiều.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&nbsp;</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Từ gam m&agrave;u trung t&iacute;nh cho tới d&aacute;ng &ocirc;m s&aacute;t, kiểu cổ đức thanh lịch, măng-s&eacute;t bản rộng. Tất cả những chi tiết đ&oacute; nhằm ph&ocirc; diễn sự nam t&iacute;nh, mạnh mẽ.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Sơ mi được l&agrave;m từ chất liệu cotton lycra mềm mại, tho&aacute;ng m&aacute;t được tăng th&ecirc;m khả năng co gi&atilde;n gi&uacute;p c&aacute;c ch&agrave;ng năng động hơn</span></p>', 'http://localhost/projectbkap/uploads/mix-ao-len-ao-so-mi-nam.jpg', '1', '1478153888', '1478153888');
INSERT INTO news VALUES ('9', 'Bảo quản áo khoác đúng cách để trang phục bền đẹp như mới', 'Tủ đồ quần áo thu đông của bạn sẽ có rất nhiều loại áo khoác và tất nhiên, cách bảo quản giữa chúng là khác nhau. Bạn có thể treo mắc chiếc áo khoác jeans, nhưng với áo khoác len cardigan thì lại không thể làm như vậy. Ai cũng muốn những chiếc áo khoác củ', '<p><span style=\"font-family: arial, helvetica, sans-serif;\">Tủ đồ <a href=\"http://canifa.com/thoi-trang-thu-dong.html\" target=\"_blank\">quần &aacute;o thu đ&ocirc;ng</a> của bạn sẽ c&oacute; rất nhiều loại &aacute;o kho&aacute;c v&agrave; tất nhi&ecirc;n, c&aacute;ch bảo quản giữa ch&uacute;ng l&agrave; kh&aacute;c nhau. Bạn c&oacute; thể treo mắc chiếc &aacute;o kho&aacute;c jeans, nhưng với &aacute;o kho&aacute;c len cardigan th&igrave; lại kh&ocirc;ng thể l&agrave;m như vậy. Ai cũng muốn những chiếc &aacute;o kho&aacute;c của m&igrave;nh lu&ocirc;n mới mẻ, bền đẹp; nhưng kh&ocirc;ng phải ai cũng biết c&aacute;ch để l&agrave;m điều đ&oacute;.</span></p>\r\n<h2><span style=\"font-family: arial, helvetica, sans-serif;\">C&aacute;ch bảo quản &ldquo;chuẩn kh&ocirc;ng cần chỉnh&rdquo; để c&aacute;c loại &aacute;o kho&aacute;c bền đẹp như mới</span></h2>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&ldquo;Của bền th&igrave; tại người&rdquo;. Việc bảo quản đ&uacute;ng c&aacute;ch c&aacute;c loại quần &aacute;o thời trang n&oacute;i ri&ecirc;ng v&agrave; &aacute;o kho&aacute;c nam, nữ n&oacute;i ri&ecirc;ng gi&uacute;p k&eacute;o d&agrave;i thời gian sử dụng của sản phẩm.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">B&agrave;i viết dưới đ&acirc;y sẽ cung cấp cho bạn c&aacute;ch đ&uacute;ng đắn nhất để bảo quản c&aacute;c loại &aacute;o kho&aacute;c.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\">Bảo quản &aacute;o kho&aacute;c jeans</span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&Aacute;o kho&aacute;c jeans lu&ocirc;n mang đến cho bạn sự trẻ trung, năng động. &Aacute;o kho&aacute;c jeans cũng rất dễ phối đồ n&ecirc;n n&oacute; trở th&agrave;nh item kh&ocirc;ng thể thiếu đối với người mặc c&aacute; t&iacute;nh. Tuy vậy, nếu kh&ocirc;ng biết c&aacute;ch bảo quản th&igrave; item n&agrave;y c&oacute; thể bị nhanh bạc m&agrave;u, sờn vải hoặc nhăn nheo.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/bao-quan-ao-khoac-jeans.jpg\" alt=\"bao-quan-ao-khoac-jeans\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Kh&oacute; c&oacute; thể phủ nhận được vẻ đẹp phong c&aacute;ch v&agrave; c&aacute; t&iacute;nh m&agrave; những mẫu &aacute;o kho&aacute;c jeans mang lại cho người mặc. Mặc d&ugrave; kh&aacute; &ldquo;dễ t&iacute;nh&rdquo; trong việc kết hợp trang phục nhưng &aacute;o kho&aacute;c jeans vẫn cần phải được bảo quản đ&uacute;ng c&aacute;ch. Mua sản phẩm tại: <a href=\"http://canifa.com/ao-khoac-jeans-nam-sub.html\" target=\"_blank\">Nam</a> / <a href=\"http://canifa.com/ao-khoac-jeans-nu-sub.html\" target=\"_blank\">Nữ</a> / <a href=\"http://canifa.com/ao-khoac-jeans-be-trai-sub.html\" target=\"_blank\">B&eacute; trai</a> / <a href=\"http://canifa.com/ao-khoac-jeans-be-gai-sub.html\" target=\"_blank\">B&eacute; g&aacute;i</a></em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Để &aacute;o kho&aacute;c jeans bền đẹp như mới, bạn h&atilde;y l&agrave;m th&ecirc;m hướng dẫn sau:</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Kh&ocirc;ng giặt &aacute;o kho&aacute;c jeans nhiều lần. Một chiếc &aacute;o kho&aacute;c jeans bạn c&oacute; thể mặc lại đến lần thứ 2 nếu bạn kh&ocirc;ng đến những nơi qu&aacute; bụi bặm. Tuy nhi&ecirc;n, để tr&aacute;nh tủ đồ của bạn c&oacute; m&ugrave;i kh&oacute; chịu, bạn n&ecirc;n lộn lại &aacute;o kho&aacute;c v&agrave; phơi ra ngo&agrave;i nắng cho tho&aacute;ng kh&iacute;, lần sau mặc lại chiếc &aacute;o kho&aacute;c jeans vẫn thơm tho như thường.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Kh&ocirc;ng phơi &aacute;o kho&aacute;c jeans ở những nơi c&oacute; nắng gắt. Chất liệu vải jeans thường rất cứng. Khi được phơi dưới nhiệt độ cao, ch&uacute;ng sẽ bị quắt lại, g&acirc;y r&aacute;t da khi mặc. V&igrave; vậy, h&atilde;y phơi &aacute;o kho&aacute;c jeans ở những nơi tho&aacute;ng gi&oacute;, tr&aacute;nh &aacute;nh nắng trực tiếp để bảo vệ l&agrave;n da của bạn. Lưu &yacute; khi phơi &aacute;o kho&aacute;c, bạn phải giũ phẳng ch&uacute;ng ra để &aacute;o kh&ocirc;ng bị nhăn nh&eacute;!</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">D&ugrave;ng sữa tắm, dầu gội đầu để giặt &aacute;o kho&aacute;c jeans. Những chiếc &aacute;o kho&aacute;c jeans sẽ rất nhanh phai m&agrave;u nếu bạn sử dụng bột giặt thường để l&agrave;m sạch ch&uacute;ng.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>C&aacute;ch tốt nhất để giữ được m&agrave;u như mới của &aacute;o kho&aacute;c jeans, bạn n&ecirc;n sử dụng sữa tắm hoặc dầu gội đầu để giặt ch&uacute;ng. Nếu cảm thấy như vậy l&agrave; hơi tốn k&eacute;m, bạn cũng c&oacute; thể mua một cục x&agrave; b&ocirc;ng rẻ tiền để giữ m&agrave;u chiếc &aacute;o kho&aacute;c jean của m&igrave;nh khi giặt.</em></span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\">C&aacute;ch bảo quản &aacute;o kho&aacute;c len</span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">&Aacute;o kho&aacute;c len hay d&ograve;ng &aacute;o cardigan trở n&ecirc;n rất HOT trong m&ugrave;a thu đ&ocirc;ng. Chắc hẳn c&aacute;c đều sở hữu &iacute;t nhất một chiếc &aacute;o kho&aacute;c len cho bản th&acirc;n m&igrave;nh. &Aacute;o kho&aacute;c len cũng l&agrave; một m&oacute;n đồ thời trang kh&aacute; dễ mặc, c&oacute; thể thay đổi nhiều phong c&aacute;ch khi biết c&aacute;ch phối đồ linh hoạt.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/bao-quan-ao-khoac-len.jpg\" alt=\"bao-quan-ao-khoac-len\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>&Aacute;o kho&aacute;c len kh&ocirc;ng &ldquo;k&eacute;n chọn&rdquo; người mặc. Ai cũng c&oacute; thể trở l&ecirc;n rất thời trang, ấm &aacute;p với trang phục n&agrave;y. Tuy nhi&ecirc;n, nếu kh&ocirc;ng biết c&aacute;ch bảo quản, &aacute;o kho&aacute;c len dễ d&agrave;ng bị bai d&atilde;o, mất d&aacute;ng chỉ sau một m&ugrave;a lạnh. Mua sản phẩm tại: <a href=\"http://canifa.com/ao-khoac-len-nam-sub.html\" target=\"_blank\">Nam</a> / <a href=\"http://canifa.com/ao-khoac-len-nu-sub.html\" target=\"_blank\">Nữ</a> / <a href=\"http://canifa.com/ao-khoac-len-be-trai-sub.html\" target=\"_blank\">B&eacute; trai</a> / <a href=\"http://canifa.com/ao-khoac-len-be-gai-sub.html\" target=\"_blank\">B&eacute; g&aacute;i</a></em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>V&igrave; vậy, h&atilde;y biết c&aacute;ch bảo vệ chiếc &aacute;o len của m&igrave;nh với những mẹo bảo quản sau:</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Phơi đ&uacute;ng c&aacute;ch: Đầu ti&ecirc;n, bạn d&ugrave;ng chiếc khăn b&ocirc;ng cuộn chiếc &aacute;o len lại rồi mới vắt nước. Nếu kh&ocirc;ng c&oacute; khăn b&ocirc;ng, bạn phải b&oacute;p nhẹ nh&agrave;ng từ tr&ecirc;n xuống dưới để &aacute;o len r&aacute;o nước. Tuyệt đối kh&ocirc;ng được vắt &aacute;o kho&aacute;c len như vắt quần &aacute;o th&ocirc;ng thường. Khi phơi, bạn cũng phải hết sức ch&uacute; &yacute;. Tuyệt đối kh&ocirc;ng được phơi ch&uacute;ng tr&ecirc;n m&oacute;c, s&agrave;o. Thay v&agrave;o đ&oacute;, những cho &aacute;o kho&aacute;c len v&agrave;o t&uacute;i phơi đồ hoặc trải ch&uacute;ng ra mặt phẳng, nơi tho&aacute;ng m&aacute;t sẽ gi&uacute;p giữ được d&aacute;ng &aacute;o trọn vẹn như mới.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Gấp &aacute;o gọn g&agrave;ng, cất v&agrave;o tủ: D&ugrave; l&uacute;c kh&ocirc; hay l&uacute;c ướt th&igrave; việc treo m&oacute;c &aacute;o len vẫn l&agrave;m ch&uacute;ng phải &ldquo;kh&oacute; chịu&rdquo; bởi rất dễ bị mất d&aacute;ng, bai d&atilde;o. V&igrave; vậy, h&atilde;y gấp ch&uacute;ng gọn g&agrave;ng lại, để c&ugrave;ng với những m&oacute;n đồ mềm mại kh&aacute;c như v&aacute;y hoa, &aacute;o ph&ocirc;ng&hellip; Đừng để ch&uacute;ng lại gần những m&oacute;n đồ nhiều c&uacute;c, hay hạt đ&iacute;nh bởi rất dễ để những sợi len bị bung ra nếu như bị ch&uacute;ng mắc v&agrave;o.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Giữ khoảng c&aacute;ch giữa c&aacute;c lần mặc: Một chiếc &aacute;o kho&aacute;c len b&oacute; s&aacute;t người c&oacute; thể bị bai d&atilde;o một c&aacute;ch nhanh ch&oacute;ng nếu bạn sử dụng ch&uacute;ng qu&aacute; thường xuy&ecirc;n. H&atilde;y đảm bảo rằng thời gian giữa c&aacute;c lần mặc c&aacute;ch nhau khoảng 24h.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\">C&aacute;ch bảo quản &aacute;o kho&aacute;c da</span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Kh&ocirc;ng chỉ l&agrave; m&oacute;n đồ giữ ấm ho&agrave;n hảo, &aacute;o kho&aacute;c da c&ograve;n mang đến một phong c&aacute;ch rất c&aacute; t&iacute;nh, lịch l&atilde;m cho người mặc. V&igrave; vậy, sở hữu một chiếc &aacute;o kho&aacute;c da trong tủ đồ sẽ gi&uacute;p bạn đa dạng h&oacute;a được phong c&aacute;ch ăn mặc.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/bao-quan-ao-khoac-da.jpg\" alt=\"bao-quan-ao-khoac-da\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Chi ph&iacute; đầu tư cho một chiếc &aacute;o kho&aacute;c da kh&ocirc;ng hề nhỏ. Do đ&oacute;, việc sử dụng, bảo quản đ&uacute;ng c&aacute;ch để trang phục lu&ocirc;n bền v&agrave; đẹp l&agrave; v&ocirc; c&ugrave;ng quan trọng.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Bạn muốn chiếc &aacute;o da được sử dụng l&acirc;u d&agrave;i v&agrave; lu&ocirc;n tr&ocirc;ng như mới chứ? H&atilde;y l&agrave;m theo những mẹo dưới đ&acirc;y:</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Treo &aacute;o tr&ecirc;n mắc: Để giữ &aacute;o da được giữ d&aacute;ng v&agrave; kh&ocirc;ng tạo n&ecirc;n c&aacute;c nếp gấp, bạn n&ecirc;n treo &aacute;o bằng những chiếc m&oacute;c rộng.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Tr&aacute;nh &aacute;nh s&aacute;ng mặt trời v&agrave; nguồn nhiệt trực tiếp: Nhiệt độ qu&aacute; cao sẽ dễ l&agrave;m &aacute;o da bị nứt nẻ, nổ m&agrave;u. Khi tiếp x&uacute;c qu&aacute; l&acirc;u dưới &aacute;nh s&aacute;ng mặt trời, &aacute;o da dễ xuất hiện th&ecirc;m c&aacute;c đốm m&agrave;u kh&aacute;c.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Kh&ocirc;ng bảo quản &aacute;o da trong t&uacute;i nhựa hoặc hộp đ&oacute;ng k&iacute;n: &Aacute;o da sẽ được bảo quản tốt hơn trong điều kiện kh&ocirc;ng kh&iacute; kh&ocirc; v&agrave; nhiệt độ ph&ugrave; hợp. V&igrave; vậy, m&ocirc;i trường l&yacute; tưởng để cất giữ ch&uacute;ng l&agrave; tủ quần &aacute;o nh&agrave; bạn. Khi m&ugrave;a đ&ocirc;ng kết th&uacute;c, bạn vẫn n&ecirc;n treo ch&uacute;ng trong tủ thay v&igrave; tống v&agrave;o hộp, t&uacute;i v&agrave; cất đi như những loại &aacute;o kh&aacute;c.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Giữ &aacute;o kho&aacute;c da tr&aacute;nh khỏi thuốc diệt c&ocirc;n tr&ugrave;ng: Trong tủ quần &aacute;o của một số gia đ&igrave;nh thường để băng phiến để tr&aacute;nh mối mọt. Tuy nhi&ecirc;n, nếu c&oacute; mặt chiếc &aacute;o da trong tủ đồ, bạn n&ecirc;n bỏ t&uacute;i băng phiến ra. &Aacute;o da rất dễ hấp thụ ch&uacute;ng v&agrave; g&acirc;y ra m&ugrave;i kh&oacute; chịu.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\">Mẹo bảo quản c&aacute;c loại &aacute;o kho&aacute;c nỉ v&agrave; dạ</span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/bao-quan-ao-khoac-ni.jpg\" alt=\"bao-quan-ao-khoac-ni\" /></em></span><br /><span style=\"font-family: arial, helvetica, sans-serif;\"><em>&Aacute;o kho&aacute;c chất liệu nỉ, dạ l&agrave; những m&oacute;n đồ thời trang m&agrave; hầu hết người ti&ecirc;u d&ugrave;ng Việt n&agrave;o cũng c&oacute; trong tủ đồ. Tuy nhi&ecirc;n, loại &aacute;o kho&aacute;c n&agrave;y rất dễ bị x&ugrave; l&ocirc;ng v&agrave; dễ b&aacute;m bụi. Do đ&oacute;, việc chăm s&oacute;c trang phục cũng đ&ograve;i hỏi sự tỉ mỉ nhất định. Mua sản phẩm n&agrave;y tại: <a href=\"http://canifa.com/ao-khoac-nam-cate.html\" target=\"_blank\">&Aacute;o kho&aacute;c nam</a> / <a href=\"http://canifa.com/ao-khoac-nu-cate.html\" target=\"_blank\">&Aacute;o kho&aacute;c nữ</a> / <a href=\"http://canifa.com/ao-khoac-be-trai-cate.html\" target=\"_blank\">&Aacute;o kho&aacute;c b&eacute; trai</a> / <a href=\"http://canifa.com/ao-khoac-be-gai-cate.html\" target=\"_blank\">&Aacute;o kho&aacute;c b&eacute; g&aacute;i</a><br /></em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><strong>Để lu&ocirc;n giữ cho &aacute;o kho&aacute;c nỉ v&agrave; dạ tr&ocirc;ng mới mẻ, sạch sẽ th&igrave; bạn cần biết một số mẹo bảo quản sau:</strong></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Mặc &aacute;o cao cổ b&ecirc;n trong lớp &aacute;o dạ: Mồ h&ocirc;i v&agrave; bụi bẩn tr&ecirc;n cơ thể rất dễ b&aacute;m bẩn ở phần cổ của &aacute;o dạ, khiến bạn kh&oacute; giặt sạch. V&igrave; vậy, h&atilde;y mặc chiếc &aacute;o cao cổ b&ecirc;n trong để tr&aacute;nh t&igrave;nh trạng n&agrave;y.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Giặt kh&ocirc; &aacute;o dạ: Đối với &aacute;o dạ, bạn kh&ocirc;ng n&ecirc;n giặt ướt v&igrave; rất dễ l&agrave;m hỏng d&aacute;ng &aacute;o. V&igrave; vậy, h&atilde;y sử dụng một chiếc khăn ẩm v&agrave; b&agrave;n l&agrave;. Đặt chiếc khăn ẩm l&ecirc;n &aacute;o rồi l&agrave; bằng b&agrave;n l&agrave;. Bụi bẩn sẽ theo hơi nước bay l&ecirc;n, thấm v&agrave;o chiếc khăn mặt. Vậy l&agrave; chiếc &aacute;o dạ của bạn tr&ocirc;ng vẫn như mới rồi.</span></p>\r\n<h3><span style=\"font-family: arial, helvetica, sans-serif;\">C&aacute;ch bảo quản &aacute;o kho&aacute;c l&ocirc;ng vũ</span></h3>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Với đặc điểm nhẹ v&agrave; giữ ấm tốt, &aacute;o kho&aacute;c l&ocirc;ng vũ được nhiều người lựa chọn để mặc trong m&ugrave;a đ&ocirc;ng. Tuy nhi&ecirc;n, nếu kh&ocirc;ng biết c&aacute;ch bảo quản, &aacute;o l&ocirc;ng vũ dễ bị bết l&ocirc;ng, xỉn m&agrave;u.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><img src=\"http://canifa.com/media/wysiwyg/SEOTeam/bao-quan-ao-khoac-long-vu.png\" alt=\"bao-quan-ao-khoac-long-vu\" /></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">CANIFA đ&atilde; c&oacute; một b&agrave;i viết rất chi tiết về <a href=\"http://canifa.com/cach-cham-soc-ao-long-vu-sieu-nhe.html\" target=\"_blank\">c&aacute;ch bảo quản &aacute;o kho&aacute;c l&ocirc;ng vũ</a>. Với cẩm nang n&agrave;y, bạn kh&ocirc;ng cần phải đọc th&ecirc;m bất kỳ t&agrave;i liệu n&agrave;o kh&aacute;c bởi n&oacute; đ&atilde; qua kiểm nghiệm nhiều lần n&ecirc;n rất ch&iacute;nh x&aacute;c.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Tr&ecirc;n đ&acirc;y l&agrave; một số mẹo bảo quản c&aacute;c loại &aacute;o kho&aacute;c thời trang th&ocirc;ng dụng. H&atilde;y lu&ocirc;n l&agrave;m cho chiếc &aacute;o kho&aacute;c của m&igrave;nh được mới mẻ v&agrave; bền đẹp nh&eacute;! Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng!</em></span></p>', 'http://localhost/projectbkap/uploads/bao-quan-ao-khoac-long-vu.png', '1', '1478154017', '1478154017');
INSERT INTO news VALUES ('10', 'Color Block - Nét chấm phá ngày đông', 'Color Block, xu hướng thời trang có lịch sử gần 40 tuổi luôn khuấy đảo sàn diễn thời trang quốc tế trong những năm gần đây. Sự góp mặt của các ông lớn như Paul Smith, Kenzo...trong mùa Thu Đông 2016 càng làm cho cuộc vui thêm sôi động.', '<p><span style=\"font-family: arial, helvetica, sans-serif;\"><em>Color Block, xu hướng thời trang c&oacute; lịch sử gần 40 tuổi lu&ocirc;n khuấy đảo s&agrave;n diễn thời trang quốc tế trong những năm gần đ&acirc;y. Sự g&oacute;p mặt của c&aacute;c &ocirc;ng lớn như Paul Smith, Kenzo...trong m&ugrave;a Thu Đ&ocirc;ng 2016 c&agrave;ng l&agrave;m cho cuộc vui th&ecirc;m s&ocirc;i động.</em></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">H&ograve;a chung nhịp đập thời trang thế giới, CANIFA ra mắt BST Color Block - N&eacute;t chấm ph&aacute; ng&agrave;y đ&ocirc;ng tr&ecirc;n chất liệu len sợi kinh điển d&agrave;nh cho nam giới v&agrave; ph&aacute;i đẹp.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Lấy cảm hứng từ những mảng m&agrave;u color block hiện đại xen lẫn phong c&aacute;ch tối giản h&oacute;a, c&aacute;c trang phục mang tới vẻ đẹp tinh tế, năng động của tuổi trẻ.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><img src=\"http://canifa.com/media/wysiwyg/colorblock-blog.jpg\" alt=\"\" /></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Những gam m&agrave;u chủ đạo của m&ugrave;a đ&ocirc;ng như xanh r&ecirc;u, v&agrave;ng đất, xanh cổ vịt được tận dụng tối đa tạo cảm gi&aacute;c ấm &aacute;p cho trang phục. C&aacute;c đường cắt mạnh mẽ tạo th&agrave;nh c&aacute;c khối m&agrave;u sắc cạnh, c&aacute; t&iacute;nh cho nam giới.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Mặc d&ugrave; vậy nhưng ch&uacute;ng lại rất dễ d&agrave;ng mix với quần jeans, &aacute;o kho&aacute;c bomber, hay măng t&ocirc; đơn sắc.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><img src=\"http://canifa.com/media/wysiwyg/8te16w022-33.jpg\" alt=\"\" /></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Với trang phục nữ th&igrave; gam m&agrave;u pastel nhẹ nh&agrave;ng như xanh mint, hồng cam lại được sử dụng nhiều hơn. B&ecirc;n cạnh đ&oacute; c&aacute;c đường cắt cũng kh&ocirc;ng qu&aacute; mạnh dạn m&agrave; nhẹ nh&agrave;ng chia th&agrave;nh c&aacute;c khối m&agrave;u đối lập, nổi bật.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\"><img src=\"http://canifa.com/media/wysiwyg/6te16w025-23.jpg\" alt=\"\" /></span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Trong suốt 20 năm gắn b&oacute; với gia đ&igrave;nh Việt, c&aacute;c sản phẩm len sợi của CANIFA vẫn lu&ocirc;n l&agrave; một niềm tự h&agrave;o đ&aacute;ng để được nhắc đến mỗi khi đ&ocirc;ng về v&agrave; BST n&agrave;y cũng kh&ocirc;ng ngoại lệ.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Chỉn chu từng đường kim mũi dệt, c&aacute;c sản phẩm c&oacute; nguồn gốc từ sợi cotton được xử l&yacute; chau chuốt tạo n&ecirc;n bề mặt len &oacute;ng mượt, mềm mại v&agrave; ấm &aacute;p.</span></p>\r\n<p><span style=\"font-family: arial, helvetica, sans-serif;\">Bạn h&atilde;y tự m&igrave;nh trải nghiệm sự tuyệt vời của d&ograve;ng sản phẩm len sợi chất lượng v&agrave; thời trang của CANIFA nh&eacute;.</span></p>', 'http://localhost/projectbkap/uploads/colorblock-blog.jpg', '1', '1478154073', '1478154073');

-- ----------------------------
-- Table structure for `payment`
-- ----------------------------
DROP TABLE IF EXISTS `payment`;
CREATE TABLE `payment` (
  `payment_id` int(11) NOT NULL AUTO_INCREMENT,
  `payment_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `status` smallint(6) NOT NULL DEFAULT '1',
  `created_at` int(11) NOT NULL,
  `updated_at` int(11) NOT NULL,
  PRIMARY KEY (`payment_id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

-- ----------------------------
-- Records of payment
-- ----------------------------
INSERT INTO payment VALUES ('1', 'Cash on Delivery', '1', '1478150927', '1479539026');
INSERT INTO payment VALUES ('2', 'Via Paypal', '1', '1478150986', '1478150986');

-- ----------------------------
-- Table structure for `product`
-- ----------------------------
DROP TABLE IF EXISTS `product`;
CREATE TABLE `product` (
  `product_id` int(11) NOT NULL AUTO_INCREMENT,
  `product_name` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `product_image` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `cat_id` int(11) NOT NULL,
  `factory_id` int(11) NOT NULL,
  `price` int(11) NOT NULL,
  `saleoff` int(11) DEFAULT NULL,
  `start_sale` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `end_sale` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `size_id` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `color_id` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `description` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `content` text COLLATE utf8_unicode_ci,
  `status` smallint(6) NOT NULL DEFAULT '1',
  `created_at` int(11) NOT NULL,
  `updated_at` int(11) NOT NULL,
  PRIMARY KEY (`product_id`)
) ENGINE=InnoDB AUTO_INCREMENT=39 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

