INSERT INTO product VALUES ('1', 'NIKE ELITE', 'http://localhost/projectbkap/uploads/elite-mens-basketball-shorts%20(1).jpg', '12', '3', '50', '2', '07-11-2016', '12-11-2016', 'a:4:{i:0;s:1:\"1\";i:1;s:1:\"2\";i:2;s:1:\"3\";i:3;s:1:\"4\";}', 'a:2:{i:0;s:1:\"4\";i:1;s:1:\"5\";}', 'The Nike Elite Mens Basketball Shorts help keep you dry, comfortable and moving freely on the court with sweat-wicking fabric and vented hems.', '<p>The Nike Elite Mens Basketball Shorts&nbsp;help keep you dry, comfortable and moving freely on the court with sweat-wicking fabric and vented hems.</p>', '1', '1478483839', '1478483839');
INSERT INTO product VALUES ('2', 'USAB NIKE REPLICA', 'http://localhost/projectbkap/uploads/usab-replica-mens-basketball-shorts.jpg', '12', '4', '80', '5', '07-11-2016', '12-11-2016', 'a:3:{i:0;s:1:\"2\";i:1;s:1:\"3\";i:2;s:1:\"4\";}', 'a:2:{i:0;s:1:\"4\";i:1;s:1:\"5\";}', 'The USAB Nike Replica Mens Basketball Shorts deliver performance comfort and breathability on the court', '<p>The&nbsp;USAB Nike Replica Mens Basketball Shorts&nbsp;deliver performance comfort and breathability on the court</p>', '1', '1478673916', '1478673916');
INSERT INTO product VALUES ('3', 'NIKE VAPOR ENERGY', 'http://localhost/projectbkap/uploads/vapor-energy-backpack.jpg', '13', '4', '80', null, '', '', 'a:1:{i:0;s:1:\"4\";}', 'a:3:{i:0;s:1:\"1\";i:1;s:1:\"4\";i:2;s:1:\"5\";}', 'The Nike Vapor Energy Backpack offers diverse storage options and comfortable carrying with multiple specialized compartments and adjustable Max Air should', '<p>The Nike Vapor Energy Backpack&nbsp;offers diverse storage options and comfortable carrying with multiple specialized compartments and adjustable Max Air should</p>', '1', '1478684799', '1478684799');
INSERT INTO product VALUES ('4', 'MANCHESTER UNITED FC HOME AUTHENTIC JERSEY', 'http://localhost/projectbkap/uploads/AI6719_21_hover_model.jpg', '13', '3', '90', '3', '07-11-2016', '30-11-2016', 'a:4:{i:0;s:1:\"1\";i:1;s:1:\"2\";i:2;s:1:\"3\";i:3;s:1:\"4\";}', 'a:1:{i:0;s:1:\"3\";}', 'Break through the defence in this mens football jersey that celebrates the long winning tradition of Manchester United. The same shirt th', '<p>Break through the defence in this mens football jersey that celebrates the long winning tradition of Manchester United. The same shirt th</p>\r\n<p><img src=\"http://localhost/projectbkap/uploads/AI6719_22_model.jpg?1478484191925\" alt=\"\" width=\"500\" height=\"500\" /></p>', '1', '1478484196', '1478484196');
INSERT INTO product VALUES ('5', 'REAL MADRID HOME ANTHEM JACKET', 'http://localhost/projectbkap/uploads/AP1841_21_model.jpg', '13', '5', '149', null, '', '', 'a:4:{i:0;s:1:\"1\";i:1;s:1:\"2\";i:2;s:1:\"3\";i:3;s:1:\"4\";}', 'a:2:{i:0;s:1:\"4\";i:1;s:1:\"5\";}', 'Celebrate the winning tradition of los Blancos in this mens football jacket. Inspired by the clean lines of Real Madrids home kit, this f', '<p>Celebrate the winning tradition of los Blancos in this mens football jacket. Inspired by the clean lines of Real Madrids home kit, this f</p>\r\n<p><img src=\"http://localhost/projectbkap/uploads/AP1841_23_hover_model.jpg?1478484389032\" alt=\"\" width=\"500\" height=\"500\" /></p>', '1', '1478484392', '1478484392');
INSERT INTO product VALUES ('6', 'NEW! Push-Up Bra', 'http://localhost/projectbkap/uploads/V594383.jpg', '9', '6', '60', null, '', '', 'a:3:{i:0;s:1:\"1\";i:1;s:1:\"2\";i:2;s:1:\"3\";}', 'a:3:{i:0;s:1:\"3\";i:1;s:1:\"4\";i:2;s:1:\"5\";}', 'Our #1 push-up is sexier than ever with plush, even softer padding for the lift and cleavage you love and a more natural feel, plus edges that disappear under clothes.</span', '<p>Our #1 push-up is sexier than ever with plush, even softer padding for the lift and cleavage you love and a more natural feel, plus edges that disappear under clothes.&lt;/span</p>', '1', '1478484577', '1478484577');
INSERT INTO product VALUES ('7', 'NEW! Add-2-Cups Push-Up Bra', 'http://localhost/projectbkap/uploads/V594435.jpg', '9', '7', '59', null, '', '', 'a:3:{i:0;s:1:\"1\";i:1;s:1:\"2\";i:2;s:1:\"3\";}', 'a:3:{i:0;s:1:\"3\";i:1;s:1:\"4\";i:2;s:1:\"5\";}', 'The ultimate lift-loving push-up is better than ever with even softer padding for the most cleavage and fullness and a U-shaped ballet back for a flawless fit.', '<p>The ultimate lift-loving push-up is better than ever with even softer padding for the most cleavage and fullness and a U-shaped ballet back for a flawless fit.</p>\r\n<p><img src=\"http://localhost/projectbkap/uploads/V594435_CROP1.jpg?1478485633625\" alt=\"\" width=\"404\" height=\"539\" /></p>', '1', '1478485637', '1478485637');
INSERT INTO product VALUES ('8', 'Racerback Push-Up Bra', 'http://localhost/projectbkap/uploads/V583447_RCGG2.jpg', '9', '6', '32', null, '', '', 'a:2:{i:0;s:1:\"2\";i:1;s:1:\"3\";}', 'a:4:{i:0;s:1:\"2\";i:1;s:1:\"3\";i:2;s:1:\"4\";i:3;s:1:\"5\";}', 'Our cotton push-up is softer than ever with lightweight padding and a sleek shape designed to disappear under clothes.', '<p>Our cotton push-up is softer than ever with lightweight padding and a sleek shape designed to disappear under clothes.</p>\r\n<p><img src=\"http://localhost/projectbkap/uploads/V586839.jpg?1478485950390\" alt=\"\" width=\"404\" height=\"539\" /></p>', '1', '1478485953', '1478485953');
INSERT INTO product VALUES ('9', 'NEW! Push-Up Bra 03', 'http://localhost/projectbkap/uploads/V583443_RC2P5_EDIT.jpg', '9', '6', '59', null, '', '', 'a:3:{i:0;s:1:\"1\";i:1;s:1:\"2\";i:2;s:1:\"3\";}', 'a:3:{i:0;s:1:\"3\";i:1;s:1:\"4\";i:2;s:1:\"5\";}', 'Our most heavenly push-up ever, with softerMemory Fit padding for lots of lightweight lift, stretchier lace sides for more comfort and a smoothing U-shaped back.', '<p>Our most heavenly push-up ever, with softerMemory Fit padding for lots of lightweight lift, stretchier lace sides for more comfort and a smoothing U-shaped back.</p>\r\n<p><img src=\"http://localhost/projectbkap/uploads/V470412_OM_F.jpg?1478486043730\" alt=\"\" width=\"404\" height=\"539\" /></p>', '1', '1478486047', '1478486047');
